import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { authAPI, courseAPI, userAPI } from './services/api';
import { courseStructure } from './taskGenerators';
import * as taskGenerators from './taskGenerators';
import { useTranslation } from 'react-i18next';
import DayNavigation from './components/DayNavigation';

// Ğ¤ÑƒĞ½ĞºÑ†Ğ¸Ñ Ğ´Ğ»Ñ Ğ¿Ñ€Ğ¸Ğ¼ĞµĞ½ĞµĞ½Ğ¸Ñ Ğ¿ĞµÑ€ĞµĞ²Ğ¾Ğ´Ğ¾Ğ² Ğº Ğ·Ğ°Ğ´Ğ°Ğ½Ğ¸ÑĞ¼
const applyTranslationsToTasks = (tasks, t) => {
  if (!tasks || !Array.isArray(tasks)) return [];

  return tasks.map(task => ({
    ...task,
    question: task.translationKey ? t(task.translationKey, task.translationParams) : task.question,
    explanation: task.explanationKey ? t(task.explanationKey, task.explanationParams) : task.explanation,
    answerFormat: task.formatKey ? t(task.formatKey) : task.answerFormat
  }));
};

// ğŸŸ¢ Ğ¤Ğ£ĞĞšĞ¦Ğ˜Ğ¯ ĞŸĞ ĞĞ’Ğ•Ğ ĞšĞ˜ ĞŸĞ ĞĞ’Ğ˜Ğ›Ğ¬ĞĞĞ¡Ğ¢Ğ˜ ĞĞ¢Ğ’Ğ•Ğ¢Ğ
const isCorrectAnswerCheck = (day, index, correctAnswer, currentAnswers) => {
  if (!currentAnswers) return false;

  const userAnswer = currentAnswers[`${day}-${index}`]?.toString().trim().toLowerCase();
  const correct = correctAnswer?.toString().toLowerCase();

  if (!userAnswer || !correct) return false;

  if (correct.includes('%')) {
    const userNum = userAnswer.replace('%', '');
    const correctNum = correct.replace('%', '');
    return userNum === correctNum;
  }

  if (correct.includes('/')) {
    const [userA, userB] = userAnswer.split('/');
    const [correctA, correctB] = correct.split('/');
    return userA === correctA && userB === correctB;
  }

  return userAnswer === correct;
};

function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [showResetConfirm, setShowResetConfirm] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentDay, setCurrentDay] = useState(1);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [authError, setAuthError] = useState('');
  const [dataLoaded, setDataLoaded] = useState(false);

  // ğŸŸ¢ EFFECT Ğ”Ğ›Ğ¯ ĞĞ¢Ğ¡Ğ›Ğ•Ğ–Ğ˜Ğ’ĞĞĞ˜Ğ¯ Ğ¡ĞšĞ ĞĞ›Ğ›Ğ
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // ğŸŸ¢ ĞĞĞ”Ğ•Ğ–ĞĞĞ¯ ĞŸĞ ĞĞ’Ğ•Ğ ĞšĞ ĞĞ£Ğ¢Ğ•ĞĞ¢Ğ˜Ğ¤Ğ˜ĞšĞĞ¦Ğ˜Ğ˜
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');

      if (!token) {
        setUser({ name: t('header.guest', "Ğ“Ğ¾ÑÑ‚ÑŒ"), isLoggedIn: false });
        setLoading(false);
        return;
      }

      // ĞŸÑ€Ğ¾Ğ±ÑƒĞµĞ¼ Ğ¿Ğ¾Ğ»ÑƒÑ‡Ğ¸Ñ‚ÑŒ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»Ñ
      try {
        const response = await authAPI.getMe();
        const userData = {
          ...response.data,
          isLoggedIn: true
        };
        setUser(userData);
        localStorage.setItem('userData', JSON.stringify(userData));
      } catch (error) {
        console.log('getMe failed, checking localStorage for user data');

        // ĞŸÑ€Ğ¾Ğ±ÑƒĞµĞ¼ Ğ²Ğ¾ÑÑÑ‚Ğ°Ğ½Ğ¾Ğ²Ğ¸Ñ‚ÑŒ Ğ¸Ğ· localStorage
        const savedUser = localStorage.getItem('userData');
        if (savedUser) {
          const userData = JSON.parse(savedUser);
          setUser({ ...userData, isLoggedIn: true });
        } else {
          // Ğ¡Ğ¾Ğ·Ğ´Ğ°ĞµĞ¼ Ğ²Ñ€ĞµĞ¼ĞµĞ½Ğ½Ğ¾Ğ³Ğ¾ Ğ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»Ñ
          const tempUser = {
            id: Date.now(),
            name: 'ĞŸĞ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»ÑŒ',
            email: 'user@example.com',
            isLoggedIn: true
          };
          setUser(tempUser);
          localStorage.setItem('userData', JSON.stringify(tempUser));
        }
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setUser({ name: t('header.guest', "Ğ“Ğ¾ÑÑ‚ÑŒ"), isLoggedIn: false });
    } finally {
      setLoading(false);
    }
  }, [t]);

  // ğŸŸ¢ Ğ—ĞĞ“Ğ Ğ£Ğ—ĞšĞ ĞŸĞ Ğ˜ Ğ¡Ğ¢ĞĞ Ğ¢Ğ•
  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // ğŸŸ¢ ĞĞĞ”Ğ•Ğ–ĞĞĞ¯ Ğ—ĞĞ“Ğ Ğ£Ğ—ĞšĞ Ğ”ĞĞĞĞ«Ğ¥
  const loadUserData = useCallback(async () => {
    if (dataLoaded) return;

    try {
      // 1. Ğ¡Ğ½Ğ°Ñ‡Ğ°Ğ»Ğ° Ğ·Ğ°Ğ³Ñ€ÑƒĞ¶Ğ°ĞµĞ¼ Ğ¸Ğ· localStorage (Ğ¼Ğ³Ğ½Ğ¾Ğ²ĞµĞ½Ğ½Ğ¾)
      const savedTests = localStorage.getItem('userMathTests');
      const savedAnswers = localStorage.getItem('userMathAnswers');

      let finalTests = [];
      let finalAnswers = {};

      if (savedTests && savedAnswers) {
        try {
          const parsedTests = JSON.parse(savedTests);
          const parsedAnswers = JSON.parse(savedAnswers);

          finalTests = parsedTests.map(dayData => ({
            ...dayData,
            tasks: applyTranslationsToTasks(dayData.tasks, t)
          }));
          finalAnswers = parsedAnswers;
        } catch (parseError) {
          console.error('Error parsing localStorage data:', parseError);
          // Ğ•ÑĞ»Ğ¸ Ğ¾ÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ğ°Ñ€ÑĞ¸Ğ½Ğ³Ğ° - Ğ³ĞµĞ½ĞµÑ€Ğ¸Ñ€ÑƒĞµĞ¼ Ğ½Ğ¾Ğ²Ñ‹Ğµ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ
          throw new Error('Invalid localStorage data');
        }
      } else {
        // 2. Ğ“ĞµĞ½ĞµÑ€Ğ¸Ñ€ÑƒĞµĞ¼ Ğ½Ğ¾Ğ²Ñ‹Ğµ Ğ·Ğ°Ğ´Ğ°Ğ½Ğ¸Ñ
        finalTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
        })).filter(day => day.tasks.length > 0);

        finalAnswers = {};
      }

      // 3. Ğ£ÑÑ‚Ğ°Ğ½Ğ°Ğ²Ğ»Ğ¸Ğ²Ğ°ĞµĞ¼ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ
      setTests(finalTests);
      setAnswers(finalAnswers);

      // 4. Ğ¡Ğ¾Ñ…Ñ€Ğ°Ğ½ÑĞµĞ¼ Ğ² localStorage
      localStorage.setItem('userMathTests', JSON.stringify(finalTests));
      localStorage.setItem('userMathAnswers', JSON.stringify(finalAnswers));

      setDataLoaded(true);

      // 5. Ğ¤ĞĞĞ¾Ğ²Ğ°Ñ ÑĞ¸Ğ½Ñ…Ñ€Ğ¾Ğ½Ğ¸Ğ·Ğ°Ñ†Ğ¸Ñ Ñ ÑĞµÑ€Ğ²ĞµÑ€Ğ¾Ğ¼ Ğ´Ğ»Ñ Ğ°Ğ²Ñ‚Ğ¾Ñ€Ğ¸Ğ·Ğ¾Ğ²Ğ°Ğ½Ğ½Ñ‹Ñ… Ğ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»ĞµĞ¹
      if (user && user.isLoggedIn) {
        setTimeout(async () => {
          try {
            console.log('ğŸ”„ Starting background sync with server...');

            // Ğ—Ğ°Ğ³Ñ€ÑƒĞ¶Ğ°ĞµĞ¼ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹ Ğ¸Ğ· Ğ‘Ğ”
            const serverAnswers = await userAPI.getAnswers().catch(() => ({ data: {} }));

            // Ğ•ÑĞ»Ğ¸ Ğ½Ğ° ÑĞµÑ€Ğ²ĞµÑ€Ğµ ĞµÑÑ‚ÑŒ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹ - Ğ¸ÑĞ¿Ğ¾Ğ»ÑŒĞ·ÑƒĞµĞ¼ Ğ¸Ñ…
            if (serverAnswers.data && Object.keys(serverAnswers.data).length > 0) {
              console.log('âœ… Answers loaded from DB:', Object.keys(serverAnswers.data).length);
              setAnswers(serverAnswers.data);
              localStorage.setItem('userMathAnswers', JSON.stringify(serverAnswers.data));
            }

            // Ğ¡Ğ¾Ñ…Ñ€Ğ°Ğ½ÑĞµĞ¼ Ñ‚ĞµĞºÑƒÑ‰Ğ¸Ğµ Ñ‚ĞµÑÑ‚Ñ‹ Ğ½Ğ° ÑĞµÑ€Ğ²ĞµÑ€
            console.log('â„¹ï¸ Ğ¡Ğ¾Ñ…Ñ€Ğ°Ğ½ĞµĞ½Ğ¸Ğµ Ğ·Ğ°Ğ´Ğ°Ñ‡ Ğ²Ñ€ĞµĞ¼ĞµĞ½Ğ½Ğ¾ Ğ¾Ñ‚ĞºĞ»ÑÑ‡ĞµĞ½Ğ¾');

            console.log('âœ… Background sync completed');
          } catch (syncError) {
            console.log('Background sync failed, continuing with local data');
          }
        }, 2000);
      }

    } catch (error) {
      console.error('Error loading user data:', error);

      // ĞĞ’ĞĞ Ğ˜Ğ™ĞĞ«Ğ™ Ğ Ğ•Ğ–Ğ˜Ğœ: Ğ³ĞµĞ½ĞµÑ€Ğ¸Ñ€ÑƒĞµĞ¼ Ğ±Ğ°Ğ·Ğ¾Ğ²Ñ‹Ğµ Ğ·Ğ°Ğ´Ğ°Ğ½Ğ¸Ñ
      const emergencyTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
      })).filter(day => day.tasks.length > 0);

      setTests(emergencyTests);
      setAnswers({});
      localStorage.setItem('userMathTests', JSON.stringify(emergencyTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));

      setDataLoaded(true);
    }
  }, [user, t, dataLoaded]);

  // ğŸŸ¢ Ğ—ĞĞ“Ğ Ğ£Ğ—ĞšĞ Ğ”ĞĞĞĞ«Ğ¥ ĞŸĞ Ğ˜ Ğ˜Ğ—ĞœĞ•ĞĞ•ĞĞ˜Ğ˜ ĞŸĞĞ›Ğ¬Ğ—ĞĞ’ĞĞ¢Ğ•Ğ›Ğ¯ Ğ˜Ğ›Ğ˜ Ğ¯Ğ—Ğ«ĞšĞ
  useEffect(() => {
    if (!loading && user !== null) {
      loadUserData();
    }
  }, [user, loading, loadUserData]);

  useEffect(() => {
    if (dataLoaded && tests.length > 0) {
      // ĞĞ±Ğ½Ğ¾Ğ²Ğ»ÑĞµĞ¼ Ğ¿ĞµÑ€ĞµĞ²Ğ¾Ğ´Ñ‹ Ğ¿Ñ€Ğ¸ ÑĞ¼ĞµĞ½Ğµ ÑĞ·Ñ‹ĞºĞ°
      const updatedTests = tests.map(dayData => ({
        ...dayData,
        tasks: applyTranslationsToTasks(dayData.tasks, t)
      }));
      setTests(updatedTests);
    }
  }, [i18n.language, t]);

  // ğŸŸ¢ Ğ£Ğ›Ğ£Ğ§Ğ¨Ğ•ĞĞĞĞ¯ Ğ¤Ğ£ĞĞšĞ¦Ğ˜Ğ¯ Ğ’ĞĞ¡Ğ¡Ğ¢ĞĞĞĞ’Ğ›Ğ•ĞĞ˜Ğ¯ Ğ˜Ğ— Ğ‘ĞĞ—Ğ« Ğ”ĞĞĞĞ«Ğ¥
  const recoverFromDatabase = async () => {
    const hasAuthToken = localStorage.getItem('authToken');
    const hasLocalTests = localStorage.getItem('userMathTests');
    const hasLocalAnswers = localStorage.getItem('userMathAnswers');

    console.log('ğŸ” Ğ¡Ğ¢ĞĞ¢Ğ£Ğ¡ Ğ’ĞĞ¡Ğ¡Ğ¢ĞĞĞĞ’Ğ›Ğ•ĞĞ˜Ğ¯:', {
      hasAuthToken: !!hasAuthToken,
      hasLocalTests: !!hasLocalTests, 
      hasLocalAnswers: !!hasLocalAnswers
    });

    // ĞšĞ Ğ˜Ğ¢Ğ˜Ğ§Ğ•Ğ¡ĞšĞ˜Ğ™ Ğ¡Ğ›Ğ£Ğ§ĞĞ™: Ğ•ÑÑ‚ÑŒ Ñ‚Ğ¾ĞºĞµĞ½, Ğ½Ğ¾ Ğ½ĞµÑ‚ Ğ»Ğ¾ĞºĞ°Ğ»ÑŒĞ½Ñ‹Ñ… Ğ´Ğ°Ğ½Ğ½Ñ‹Ñ…
    if (hasAuthToken && (!hasLocalTests || !hasLocalAnswers)) {
      console.log('ğŸš¨ Ğ’ĞĞ¡Ğ¡Ğ¢ĞĞĞĞ’Ğ›Ğ•ĞĞ˜Ğ• Ğ˜Ğ— Ğ‘Ğ”: Ğ¢Ğ¾ĞºĞµĞ½ ĞµÑÑ‚ÑŒ, Ğ½Ğ¾ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ¿Ğ¾Ñ‚ĞµÑ€ÑĞ½Ñ‹!');

      try {
