function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    console.log('üîÑ EFFECT: –ü—Ä–æ–≤–µ—Ä–∫–∞ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏–∏');
    const checkAuth = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        setUser({ name: "–ì–æ—Å—Ç—å", isLoggedIn: false });
        setLoading(false);
        return;
      }

      try {
        const response = await authAPI.getMe();
        setUser({ ...response.data, isLoggedIn: true });
        console.log('‚úÖ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω:', response.data.username);
      } catch (error) {
        console.log('‚ùå –û—à–∏–±–∫–∞ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏–∏');
        setUser({ name: "–ì–æ—Å—Ç—å", isLoggedIn: false });
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  useEffect(() => {
    if (!loading && user) {
      console.log('üîÑ EFFECT: –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è, isLoggedIn:', user.isLoggedIn);
      if (user.isLoggedIn) {
        loadUserData();
      }
    }
  }, [user, loading]);

  const loadUserData = async () => {
    console.log('üì• –ù–∞—á–∞–ª–æ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è');

    try {
      // 1. –ü—Ä–æ–±—É–µ–º –∑–∞–≥—Ä—É–∑–∏—Ç—å –∑–∞–¥–∞–Ω–∏—è –∏–∑ –ë–î
      console.log('üîÑ –ó–∞–ø—Ä–æ—Å –∑–∞–¥–∞–Ω–∏–π –∏–∑ –ë–î...');
      const serverTasks = await userAPI.getAllTasks();
      console.log('üì• –û—Ç–≤–µ—Ç –æ—Ç userAPI.getAllTasks():', serverTasks.data);

      if (serverTasks.data && Object.keys(serverTasks.data).length > 0) {
        console.log('‚úÖ –ó–∞–¥–∞–Ω–∏—è –∑–∞–≥—Ä—É–∂–µ–Ω—ã –∏–∑ –ë–î');
        const serverTests = Object.keys(serverTasks.data).map(dayStr => {
          const day = parseInt(dayStr);
          const dayTasks = serverTasks.data[dayStr];
          const dayInfo = courseStructure.find(d => d.day === day) || {
            titleKey: `days.day${day}`,
            theoryLink: null
          };

          return {
            day: day,
            titleKey: dayInfo.titleKey,
            theoryLink: dayInfo.theoryLink,
            tasks: dayTasks
          };
        });

        console.log('üîÑ –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∑–∞–¥–∞–Ω–∏—è –∏–∑ –ë–î:', serverTests.length, '–¥–Ω–µ–π');
        setTests(serverTests);
        localStorage.setItem('userMathTests', JSON.stringify(serverTests));

        // 2. –ó–∞–≥—Ä—É–∂–∞–µ–º –æ—Ç–≤–µ—Ç—ã
        console.log('üîÑ –ó–∞–ø—Ä–æ—Å –æ—Ç–≤–µ—Ç–æ–≤ –∏–∑ –ë–î...');
        const answersResponse = await userAPI.getAnswers();
        console.log('üì• –û—Ç–≤–µ—Ç –æ—Ç userAPI.getAnswers():', answersResponse.data);

        if (answersResponse.data) {
          const transformedAnswers = {};
          Object.keys(answersResponse.data).forEach(day => {
            Object.keys(answersResponse.data[day]).forEach(taskIndex => {
              const answerData = answersResponse.data[day][taskIndex];
              transformedAnswers[`${day}-${taskIndex}`] = answerData.answer || '';
            });
          });

          console.log('üîÑ –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –æ—Ç–≤–µ—Ç—ã –∏–∑ –ë–î:', Object.keys(transformedAnswers).length, '–æ—Ç–≤–µ—Ç–æ–≤');
          setAnswers(transformedAnswers);
          localStorage.setItem('userMathAnswers', JSON.stringify(transformedAnswers));
        }

      } else {
        console.log('‚ùå –í –ë–î –Ω–µ—Ç –∑–∞–¥–∞–Ω–∏–π, –Ω—É–∂–Ω–æ —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å');
        // –ó–¥–µ—Å—å –±—É–¥–µ—Ç –≥–µ–Ω–µ—Ä–∞—Ü–∏—è
      }

    } catch (error) {
      console.log('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:', error);
    }
  };

  // –û—Å—Ç–∞–ª—å–Ω–æ–π –∫–æ–¥ –≤—Ä–µ–º–µ–Ω–Ω–æ —É–ø—Ä–æ—â–µ–Ω –¥–ª—è –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∏
