    <div className="min-h-screen bg-gray-50">
      <Header user={user || { name: "–ì–æ—Å—Ç—å", isLoggedIn: false }} />

      {loading ? (
        <div className="flex items-center justify-center h-64">
          <div>–ó–∞–≥—Ä—É–∑–∫–∞...</div>
        </div>
      ) : (
        <div className="container mx-auto p-4">
          <h1 className="text-2xl font-bold mb-4">–î–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö</h1>
          <div className="bg-white p-4 rounded shadow">
            <p>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å: {user?.username || '–ì–æ—Å—Ç—å'}</p>
            <p>–°—Ç–∞—Ç—É—Å: {user?.isLoggedIn ? '–ê–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω' : '–ì–æ—Å—Ç—å'}</p>
            <p>–ó–∞–≥—Ä—É–∂–µ–Ω–æ –¥–Ω–µ–π: {tests.length}</p>
            <p>–ó–∞–≥—Ä—É–∂–µ–Ω–æ –æ—Ç–≤–µ—Ç–æ–≤: {Object.keys(answers).length}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
