            await syncProgressWithServer({}, newTests);
            console.log('ğŸ”„ Generated new tasks after cache clear');
          }
        } catch (error) {
          console.error('âŒ Recovery failed:', error);
        } finally {
          setIsSyncing(false);
        }
      }
    };

