import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

const Login = ({ onLogin, onSwitchToRegister }) => {
  const { t } = useTranslation();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('üîÑ LOGIN FORM SUBMITTED:', formData);
    onLogin(formData); // ‚úÖ –ü–ï–†–ï–î–ê–ï–ú –î–ê–ù–ù–´–ï –§–û–†–ú–´
  };

  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-md">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
        {t('auth.login.title', "–í—Ö–æ–¥ –≤ —Å–∏—Å—Ç–µ–º—É")}
      </h2>

      <form onSubmit={handleSubmit}>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('auth.login.email', "Email")}
            </label>
            <input
              type="email"
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('auth.login.password', "–ü–∞—Ä–æ–ª—å")}
            </label>
            <input
              type="password"
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-md font-medium"
          >
            {t('auth.login.submit', "–í–æ–π—Ç–∏")}
          </button>
        </div>
      </form>

      <div className="text-center mt-4">
        <button
          onClick={onSwitchToRegister}
          className="text-blue-500 hover:text-blue-600 text-sm"
        >
          {t('auth.login.noAccount', "–ù–µ—Ç –∞–∫–∫–∞—É–Ω—Ç–∞? –ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è")}
        </button>
      </div>
    </div>
  );
};

export default Login;
