import axios from 'axios';

// 🟢 УЛУЧШЕННАЯ КОНФИГУРАЦИЯ URL ДЛЯ PRODUCTION
const getAPIBaseURL = () => {
  // Для разработки используем proxy из package.json
  if (process.env.NODE_ENV === 'development') {
    return '/api'; // Прокси через React dev server
  }
  // Для продакшена - относительный путь (проксируется через NPM)
  return '/api';
};

const API_BASE_URL = getAPIBaseURL();

// Функция для получения токена
const getToken = () => {
  return localStorage.getItem('authToken');
};

// 🟢 УЛУЧШЕННЫЕ НАСТРОЙКИ AXIOS
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000, // Увеличил таймаут до 15 секунд
  withCredentials: false, // Важно для CORS
});

// 🟢 УЛУЧШЕННЫЙ INTERCEPTOR ДЛЯ ЗАПРОСОВ
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    // Обязательные headers
    config.headers['Content-Type'] = 'application/json';
    config.headers['Accept'] = 'application/json';

    console.log(`🚀 API Request: ${config.method?.toUpperCase()} ${config.url}`);

    return config;
  },
  (error) => {
    console.error('❌ Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

// 🟢 УЛУЧШЕННЫЙ INTERCEPTOR ДЛЯ ОТВЕТОВ
api.interceptors.response.use(
  (response) => {
    console.log(`✅ API Success: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    const errorDetails = {
      url: error.config?.url,
      method: error.config?.method,
      status: error.response?.status,
      statusText: error.response?.statusText,
      data: error.response?.data,
      message: error.message,
      code: error.code
    };

    console.error('❌ API Error Details:', errorDetails);

    // Обработка специфичных ошибок
    if (error.response) {
      // Сервер ответил с ошибкой
      switch (error.response.status) {
        case 401:
	  console.error('🔐 Unauthorized - token may be expired');
          break;
        case 404:
          console.error('🔍 Endpoint not found');
          break;
        case 500:
          console.error('💥 Server error');
          break;
        default:
          console.error(`⚠️ HTTP error: ${error.response.status}`);
      }
    } else if (error.request) {
      // Запрос был сделан, но ответ не получен
      console.error('🌐 Network error - no response received');
    } else {
      // Что-то пошло не так при настройке запроса
      console.error('⚙️ Request setup error:', error.message);
    }

    return Promise.reject(error);
  }
);

// 🟢 УЛУЧШЕННЫЕ API ФУНКЦИИ
export const authAPI = {
  register: (userData) => {
    console.log('📝 Register attempt:', { email: userData.email, username: userData.username });
    return api.post('/auth/register', userData);
  },
  login: (credentials) => {
    console.log('🔐 Login attempt:', { email: credentials.email });
    return api.post('/auth/login', credentials);
  },
  getMe: () => {
    console.log('👤 Get user info');
    return api.get('/users/me');
  },
};

export const courseAPI = {
  getStructure: () => {
    console.log('📚 Get course structure');
    return api.get('/course/structure');
  },
  generateTasks: (dayNumber) => {
    console.log(`🎯 Generate tasks for day ${dayNumber}`);
    return api.get(`/course/day/${dayNumber}/tasks`);
  },
  getProgress: () => {
    console.log('📊 Get user progress');
    return api.get('/course/progress');
  },

updateProgress: (day, answers, completed) => {
  console.log(`🔄 Update progress for day ${day}`, { completed });

  return api.post(`/course/day/${day}/progress`, { 
    day: parseInt(day),
    completed: parseInt(completed),
    total: 10
  });
},

  resetProgress: () => {
    console.log('🗑️ Reset progress');
    return api.delete('/course/progress');
  },
};

export const userAPI = {
  getTasks: (day) => {
    console.log(`📋 Get tasks for day ${day}`);
    return api.get(`/user/tasks/${day}`);
  },
  saveTasks: (day, tasks) => {
    console.log(`💾 Save tasks for day ${day}`, { taskCount: tasks.length });
    return api.post(`/user/tasks/${day}`, tasks);
  },


  getAnswers: (day) => {
    console.log(`📝 Get all user answers`);
    return api.get('/user/answers');
  },


//  getAnswers: (day) => {
//    const endpoint = day === 'all' ? '/user/answers' : `/user/answers/${day}`;
//    console.log(`📝 Get answers for: ${day}`);
//    return api.get(endpoint);
//  },

  saveAnswer: (day, taskIndex, answer, isCorrect = false) => {
    console.log(`✏️ Save answer for day ${day}, task ${taskIndex}`, { 
      answer, 
      isCorrect 
    });
    return api.post(`/user/answer/${day}/${taskIndex}`, { 
      answer, 
      is_correct: isCorrect 
    });
  },
};

// 🟢 ДОПОЛНИТЕЛЬНЫЕ УТИЛИТЫ
export const apiUtils = {
  // Проверка доступности API
  healthCheck: () => api.get('/health'),
  // Очистка токена при logout
  clearAuth: () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    console.log('🔓 Auth data cleared');
  },
  // Получение текущего токена
  getCurrentToken: () => getToken(),
};

export default api;
