import axios from 'axios';

// ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐÐ¯ ÐšÐžÐÐ¤Ð˜Ð“Ð£Ð ÐÐ¦Ð˜Ð¯ URL Ð”Ð›Ð¯ PRODUCTION
const getAPIBaseURL = () => {
  // Ð”Ð»Ñ Ñ€Ð°Ð·Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ¸ Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ proxy Ð¸Ð· package.json
  if (process.env.NODE_ENV === 'development') {
    return '/api'; // ÐŸÑ€Ð¾ÐºÑÐ¸ Ñ‡ÐµÑ€ÐµÐ· React dev server
  }
  // Ð”Ð»Ñ Ð¿Ñ€Ð¾Ð´Ð°ÐºÑˆÐµÐ½Ð° - Ð¾Ñ‚Ð½Ð¾ÑÐ¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ð¹ Ð¿ÑƒÑ‚ÑŒ (Ð¿Ñ€Ð¾ÐºÑÐ¸Ñ€ÑƒÐµÑ‚ÑÑ Ñ‡ÐµÑ€ÐµÐ· NPM)
  return '/api';
};

const API_BASE_URL = getAPIBaseURL();

// Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ñ‚Ð¾ÐºÐµÐ½Ð°
const getToken = () => {
  return localStorage.getItem('authToken');
};

// ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐ«Ð• ÐÐÐ¡Ð¢Ð ÐžÐ™ÐšÐ˜ AXIOS
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000, // Ð£Ð²ÐµÐ»Ð¸Ñ‡Ð¸Ð» Ñ‚Ð°Ð¹Ð¼Ð°ÑƒÑ‚ Ð´Ð¾ 15 ÑÐµÐºÑƒÐ½Ð´
  withCredentials: false, // Ð’Ð°Ð¶Ð½Ð¾ Ð´Ð»Ñ CORS
});

// ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐ«Ð™ INTERCEPTOR Ð”Ð›Ð¯ Ð—ÐÐŸÐ ÐžÐ¡ÐžÐ’
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    // ÐžÐ±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ headers
    config.headers['Content-Type'] = 'application/json';
    config.headers['Accept'] = 'application/json';

    console.log(`ðŸš€ API Request: ${config.method?.toUpperCase()} ${config.url}`);

    return config;
  },
  (error) => {
    console.error('âŒ Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

// ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐ«Ð™ INTERCEPTOR Ð”Ð›Ð¯ ÐžÐ¢Ð’Ð•Ð¢ÐžÐ’
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Success: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    const errorDetails = {
      url: error.config?.url,
      method: error.config?.method,
      status: error.response?.status,
      statusText: error.response?.statusText,
      data: error.response?.data,
      message: error.message,
      code: error.code
    };

    console.error('âŒ API Error Details:', errorDetails);

    // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° ÑÐ¿ÐµÑ†Ð¸Ñ„Ð¸Ñ‡Ð½Ñ‹Ñ… Ð¾ÑˆÐ¸Ð±Ð¾Ðº
    if (error.response) {
      // Ð¡ÐµÑ€Ð²ÐµÑ€ Ð¾Ñ‚Ð²ÐµÑ‚Ð¸Ð» Ñ Ð¾ÑˆÐ¸Ð±ÐºÐ¾Ð¹
      switch (error.response.status) {
        case 401:
	  console.error('ðŸ” Unauthorized - token may be expired');
          break;
        case 404:
          console.error('ðŸ” Endpoint not found');
          break;
        case 500:
          console.error('ðŸ’¥ Server error');
          break;
        default:
          console.error(`âš ï¸ HTTP error: ${error.response.status}`);
      }
    } else if (error.request) {
      // Ð—Ð°Ð¿Ñ€Ð¾Ñ Ð±Ñ‹Ð» ÑÐ´ÐµÐ»Ð°Ð½, Ð½Ð¾ Ð¾Ñ‚Ð²ÐµÑ‚ Ð½Ðµ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½
      console.error('ðŸŒ Network error - no response received');
    } else {
      // Ð§Ñ‚Ð¾-Ñ‚Ð¾ Ð¿Ð¾ÑˆÐ»Ð¾ Ð½Ðµ Ñ‚Ð°Ðº Ð¿Ñ€Ð¸ Ð½Ð°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐµ Ð·Ð°Ð¿Ñ€Ð¾ÑÐ°
      console.error('âš™ï¸ Request setup error:', error.message);
    }

    return Promise.reject(error);
  }
);

// ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐ«Ð• API Ð¤Ð£ÐÐšÐ¦Ð˜Ð˜
export const authAPI = {
  register: (userData) => {
    console.log('ðŸ“ Register attempt:', { email: userData.email, username: userData.username });
    return api.post('/auth/register', userData);
  },
  login: (credentials) => {
    console.log('ðŸ” Login attempt:', { email: credentials.email });
    return api.post('/auth/login', credentials);
  },
  getMe: () => {
    console.log('ðŸ‘¤ Get user info');
    return api.get('/users/me');
  },
};

export const courseAPI = {
  getStructure: () => {
    console.log('ðŸ“š Get course structure');
    return api.get('/course/structure');
  },
  generateTasks: (dayNumber) => {
    console.log(`ðŸŽ¯ Generate tasks for day ${dayNumber}`);
    return api.get(`/course/day/${dayNumber}/tasks`);
  },
  getProgress: () => {
    console.log('ðŸ“Š Get user progress');
    return api.get('/course/progress');
  },

updateProgress: (day, answers, completed) => {
  console.log(`ðŸ”„ Update progress for day ${day}`, { completed });

  return api.post(`/course/day/${day}/progress`, { 
    day: parseInt(day),
    completed: parseInt(completed),
    total: 10
  });
},

  resetProgress: () => {
    console.log('ðŸ—‘ï¸ Reset progress');
    return api.delete('/course/progress');
  },
};

export const userAPI = {
  getTasks: (day) => {
    console.log(`ðŸ“‹ Get tasks for day ${day}`);
    return api.get(`/user/tasks/${day}`);
  },
  saveTasks: (day, tasks) => {
    console.log(`ðŸ’¾ Save tasks for day ${day}`, { taskCount: tasks.length });
    return api.post(`/user/tasks/${day}`, tasks);
  },


  getAnswers: (day) => {
    console.log(`ðŸ“ Get all user answers`);
    return api.get('/user/answers');
  },


//  getAnswers: (day) => {
//    const endpoint = day === 'all' ? '/user/answers' : `/user/answers/${day}`;
//    console.log(`ðŸ“ Get answers for: ${day}`);
//    return api.get(endpoint);
//  },

  saveAnswer: (day, taskIndex, answer, isCorrect = false) => {
    console.log(`âœï¸ Save answer for day ${day}, task ${taskIndex}`, { 
      answer, 
      isCorrect 
    });
    return api.post(`/user/answer/${day}/${taskIndex}`, { 
      answer, 
      is_correct: isCorrect 
    });
  },
};

// ðŸŸ¢ Ð”ÐžÐŸÐžÐ›ÐÐ˜Ð¢Ð•Ð›Ð¬ÐÐ«Ð• Ð£Ð¢Ð˜Ð›Ð˜Ð¢Ð«
export const apiUtils = {
  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ð¾ÑÑ‚Ð¸ API
  healthCheck: () => api.get('/health'),
  // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ° Ñ‚Ð¾ÐºÐµÐ½Ð° Ð¿Ñ€Ð¸ logout
  clearAuth: () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    console.log('ðŸ”“ Auth data cleared');
  },
  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ñ‚ÐµÐºÑƒÑ‰ÐµÐ³Ð¾ Ñ‚Ð¾ÐºÐµÐ½Ð°
  getCurrentToken: () => getToken(),
};

export default api;
