import axios from 'axios';

const getAPIBaseURL = () => {
  if (process.env.NODE_ENV === 'development') {
    return '/api';
  }
  return '/api';
};

const API_BASE_URL = getAPIBaseURL();

const getToken = () => {
  return localStorage.getItem('authToken');
};

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  withCredentials: false,
});

api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    config.headers['Content-Type'] = 'application/json';
    config.headers['Accept'] = 'application/json';
    console.log(`🚀 API Request: ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('❌ Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    console.log(`✅ API Success: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('❌ API Error Details:', error);
    return Promise.reject(error);
  }
);

// 🟢 ИСПРАВЛЕННЫЕ ENDPOINTS - добавлен /api
export const authAPI = {
  register: (userData) => api.post('/api/auth/register', userData),
  login: (credentials) => api.post('/api/auth/login', credentials),
  getMe: () => api.get('/api/auth/me'),
};

export const courseAPI = {
  getStructure: () => api.get('/api/course/structure'),
  generateTasks: (dayNumber) => api.get(`/api/course/day/${dayNumber}/tasks`),
  getProgress: () => api.get('/api/course/progress'),
  updateProgress: (day, answers, completed) => 
    api.post(`/api/course/progress/${day}`, { completed_tasks: completed }),
  resetProgress: () => api.post('/api/course/reset-progress'),
};

export const userAPI = {
  getAllTasks: () => api.get('/api/users/tasks'),
  getTasks: (day) => api.get(`/api/users/tasks/${day}`),
  saveTasks: (day, tasks) => api.post(`/api/users/tasks/${day}`, tasks),
  getAnswers: () => api.get('/api/users/answers'),
  saveAnswer: (day, taskIndex, answer, isCorrect = false) => 
    api.post(`/api/users/answers/${day}/${taskIndex}`, { 
      answer, 
      is_correct: isCorrect 
    }),
};

export default api;
