import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { authAPI, courseAPI, userAPI } from './services/api';
import { courseStructure } from './taskGenerators';
import * as taskGenerators from './taskGenerators';
import { useTranslation } from 'react-i18next';
import DayNavigation from './components/DayNavigation';

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è –ø–µ—Ä–µ–≤–æ–¥–æ–≤ –∫ –∑–∞–¥–∞–Ω–∏—è–º
const applyTranslationsToTasks = (tasks, t) => {
  return tasks.map(task => ({
    ...task,
    question: task.translationKey ? t(task.translationKey, task.translationParams) : task.question,
    explanation: task.explanationKey ? t(task.explanationKey, task.explanationParams) : task.explanation,
    answerFormat: task.formatKey ? t(task.formatKey) : task.answerFormat
  }));
};

// üü¢ –§–£–ù–ö–¶–ò–Ø –î–õ–Ø –°–ò–ù–•–†–û–ù–ò–ó–ê–¶–ò–ò –ü–†–û–ì–†–ï–°–°–ê
const syncProgressWithServer = async (localAnswers, localTests) => {
  try {
    // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–µ—Å—Ç—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä
    if (localTests && localTests.length > 0) {
      for (const dayData of localTests) {
        await userAPI.saveTasks(dayData.day, dayData.tasks);
      }
    }

    // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ç–≤–µ—Ç—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä
    if (localAnswers && Object.keys(localAnswers).length > 0) {
      for (const [key, answer] of Object.entries(localAnswers)) {
        const [day, index] = key.split('-').map(Number);
        const dayData = localTests.find(d => d.day === day);
        if (dayData && dayData.tasks[index]) {
          const task = dayData.tasks[index];
          const isCorrectAnswer = isCorrectAnswerCheck(day, index, task.answer, localAnswers);
          await userAPI.saveAnswer(day, index, answer, isCorrectAnswer);
        }
      }
    }

    console.log('‚úÖ –ü—Ä–æ–≥—Ä–µ—Å—Å —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞–Ω —Å —Å–µ—Ä–≤–µ—Ä–æ–º');
    return true;
  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–∏ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞:', error);
    return false;
  }
};

// üü¢ –§–£–ù–ö–¶–ò–Ø –î–õ–Ø –ó–ê–ì–†–£–ó–ö–ò –ü–†–û–ì–†–ï–°–°–ê –° –°–ï–†–í–ï–†–ê
const loadProgressFromServer = async () => {
  try {
    const [progressResponse, answersResponse, tasksResponse] = await Promise.all([
      courseAPI.getProgress().catch(() => ({ data: {} })),
      userAPI.getAnswers('all').catch(() => ({ data: {} })),
      userAPI.getTasks('all').catch(() => ({ data: {} }))
    ]);

    const serverProgress = {
      answers: answersResponse.data || {},
      tests: tasksResponse.data || {},
      progress: progressResponse.data || {}
    };

    console.log('üì• –ü—Ä–æ–≥—Ä–µ—Å—Å –∑–∞–≥—Ä—É–∂–µ–Ω —Å —Å–µ—Ä–≤–µ—Ä–∞:', serverProgress);
    return serverProgress;
  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞ —Å —Å–µ—Ä–≤–µ—Ä–∞:', error);
    return { answers: {}, tests: {}, progress: {} };
  }
};

// üü¢ –§–£–ù–ö–¶–ò–Ø –ü–†–û–í–ï–†–ö–ò –ü–†–ê–í–ò–õ–¨–ù–û–°–¢–ò –û–¢–í–ï–¢–ê (–≤—ã–Ω–µ—Å–µ–Ω–∞ –¥–ª—è –ø–µ—Ä–µ–∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è)
const isCorrectAnswerCheck = (day, index, correctAnswer, currentAnswers) => {
  const userAnswer = currentAnswers[`${day}-${index}`]?.toString().trim().toLowerCase();
  const correct = correctAnswer.toString().toLowerCase();

  if (!userAnswer) return false;

  if (correct.includes('%')) {
    const userNum = userAnswer.replace('%', '');
    const correctNum = correct.replace('%', '');
    return userNum === correctNum;
  }

  if (correct.includes('/')) {
    const [userA, userB] = userAnswer.split('/');
    const [correctA, correctB] = correct.split('/');
    return userA === correctA && userB === correctB;
  }

  return userAnswer === correct;
};

function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [showResetConfirm, setShowResetConfirm] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentDay, setCurrentDay] = useState(1);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [authError, setAuthError] = useState('');
  const [isSyncing, setIsSyncing] = useState(false);

  // üü¢ EFFECT –î–õ–Ø –û–¢–°–õ–ï–ñ–ò–í–ê–ù–ò–Ø –°–ö–†–û–õ–õ–ê
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // üü¢ –ü–†–û–í–ï–†–ö–ê –ê–£–¢–ï–ù–¢–ò–§–ò–ö–ê–¶–ò–ò –° –°–ò–ù–•–†–û–ù–ò–ó–ê–¶–ò–ï–ô –ü–†–û–ì–†–ï–°–°–ê
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');
      const savedUser = localStorage.getItem('userData');

      if (token && savedUser) {
        const userData = JSON.parse(savedUser);
        setUser(userData);

        // üü¢ –ó–ê–ì–†–£–ñ–ê–ï–ú –ò –°–ò–ù–•–†–û–ù–ò–ó–ò–†–£–ï–ú –ü–†–û–ì–†–ï–°–° –ü–û–°–õ–ï –õ–û–ì–ò–ù–ê
        if (userData.isLoggedIn) {
          await initializeUserProgress();
        }
      } else if (token) {
        try {
          const response = await authAPI.getMe();
          const userData = {
            ...response.data,
            isLoggedIn: true
          };
          setUser(userData);
          localStorage.setItem('userData', JSON.stringify(userData));

          // üü¢ –ó–ê–ì–†–£–ñ–ê–ï–ú –ò –°–ò–ù–•–†–û–ù–ò–ó–ò–†–£–ï–ú –ü–†–û–ì–†–ï–°–°
          await initializeUserProgress();
        } catch (error) {
          console.log('getMe failed, using fallback user');
          const userData = {
            name: '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å',
            email: 'user@example.com', 
            isLoggedIn: true
          };
          setUser(userData);
          localStorage.setItem('userData', JSON.stringify(userData));
        }
      } else {
        setUser({ name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false });
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setUser({ name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false });
    } finally {
      setLoading(false);
    }
  }, [t]);

  // üü¢ –ò–ù–ò–¶–ò–ê–õ–ò–ó–ê–¶–ò–Ø –ü–†–û–ì–†–ï–°–°–ê –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø
  const initializeUserProgress = async () => {
    setIsSyncing(true);

    try {
      // 1. –ó–∞–≥—Ä—É–∂–∞–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å —Å —Å–µ—Ä–≤–µ—Ä–∞
      const serverProgress = await loadProgressFromServer();

      // 2. –ó–∞–≥—Ä—É–∂–∞–µ–º –∏–∑ localStorage
      const localTests = localStorage.getItem('userMathTests');
      const localAnswers = localStorage.getItem('userMathAnswers');

      let finalTests = [];
      let finalAnswers = {};

      // 3. –ü–†–ò–û–†–ò–¢–ï–¢ –°–ï–†–í–ï–†–ê: –µ—Å–ª–∏ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ –µ—Å—Ç—å –¥–∞–Ω–Ω—ã–µ, –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏—Ö
      if (serverProgress.tests && Object.keys(serverProgress.tests).length > 0) {
        finalTests = Object.values(serverProgress.tests).map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        }));
        finalAnswers = serverProgress.answers;

        console.log('üîÑ –ò—Å–ø–æ–ª—å–∑—É–µ–º –¥–∞–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–∞');
      } 
      // 4. –ï–°–õ–ò –ù–ê –°–ï–†–í–ï–†–ï –ù–ï–¢ –î–ê–ù–ù–´–•, –Ω–æ –µ—Å—Ç—å –≤ localStorage - —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º
      else if (localTests && localAnswers) {
        const parsedTests = JSON.parse(localTests);
        const parsedAnswers = JSON.parse(localAnswers);

        finalTests = parsedTests.map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        }));
        finalAnswers = parsedAnswers;

        // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º –ª–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–æ–º
        await syncProgressWithServer(parsedAnswers, parsedTests);
        console.log('üîÑ –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞–ª–∏ –ª–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–æ–º');
      }
      // 5. –ï–°–õ–ò –î–ê–ù–ù–´–• –ù–ï–¢ –í–û–û–ë–©–ï - –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –Ω–æ–≤—ã–µ
      else {
        finalTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator](day.taskCount, t)
        }));

        // –°–æ—Ö—Ä–∞–Ω—è–µ–º –Ω–æ–≤—ã–µ —Ç–µ—Å—Ç—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä
        for (const dayData of finalTests) {
          await userAPI.saveTasks(dayData.day, dayData.tasks);
        }
        console.log('üîÑ –°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞–ª–∏ –Ω–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è');
      }

      // 6. –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ñ–∏–Ω–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –∏ localStorage
      setTests(finalTests);
      setAnswers(finalAnswers);
      localStorage.setItem('userMathTests', JSON.stringify(finalTests));
      localStorage.setItem('userMathAnswers', JSON.stringify(finalAnswers));

    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞:', error);

      // –§–æ–ª–±—ç–∫ –Ω–∞ localStorage
      const localTests = localStorage.getItem('userMathTests');
      const localAnswers = localStorage.getItem('userMathAnswers');

      if (localTests && localAnswers) {
        const parsedTests = JSON.parse(localTests);
        const parsedAnswers = JSON.parse(localAnswers);

        setTests(parsedTests.map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        })));
        setAnswers(parsedAnswers);
      } else {
        // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –Ω–æ–≤—ã—Ö –∑–∞–¥–∞–Ω–∏–π –∫–∞–∫ –ø–æ—Å–ª–µ–¥–Ω–µ–µ —Å—Ä–µ–¥—Å—Ç–≤–æ
        const newTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator](day.taskCount, t)
        }));
        setTests(newTests);
        localStorage.setItem('userMathTests', JSON.stringify(newTests));
      }
    } finally {
      setIsSyncing(false);
    }
  };

  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // üü¢ –û–ë–†–ê–ë–û–¢–ß–ò–ö –í–•–û–î–ê –° –°–ò–ù–•–†–û–ù–ò–ó–ê–¶–ò–ï–ô
  const handleLogin = async (formData) => {
    console.log('üîê LOGIN ATTEMPT:', formData);
    setAuthError('');

    try {
      const response = await authAPI.login({ 
        email: formData.email, 
        password: formData.password 
      });
      console.log('‚úÖ LOGIN SUCCESS:', response.data);

      localStorage.setItem('authToken', response.data.access_token);
      await checkAuth(); // –≠—Ç–æ –≤—ã–∑–æ–≤–µ—Ç initializeUserProgress
      setShowLogin(false);
    } catch (error) {
      console.error('‚ùå LOGIN ERROR:', error);
      const errorMessage = error.response?.data?.detail || error.message || '–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞';
      setAuthError(errorMessage);
    }
  };

  // üü¢ –û–ë–†–ê–ë–û–¢–ß–ò–ö –†–ï–ì–ò–°–¢–†–ê–¶–ò–ò
  const handleRegister = async (formData) => {
    console.log('üìù REGISTER ATTEMPT:', formData);
    setAuthError('');

    try {
      const response = await authAPI.register({ 
        email: formData.email,
        username: formData.username, 
        password: formData.password,
        grade: formData.grade
      });
      console.log('‚úÖ REGISTER SUCCESS:', response.data);

      const loginResponse = await authAPI.login({
        email: formData.email,
        password: formData.password
      });
      localStorage.setItem('authToken', loginResponse.data.access_token);
      await checkAuth();
      setShowRegister(false);
    } catch (error) {
      console.error('‚ùå REGISTER ERROR:', error);
      const errorMessage = error.response?.data?.detail || error.message || '–û—à–∏–±–∫–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏';
      setAuthError(errorMessage);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    setUser({ name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false });
    setAnswers({});
    setTests([]);
    setAuthError('');
  };

  // üü¢ –£–õ–£–ß–®–ï–ù–ù–ê–Ø –û–ë–†–ê–ë–û–¢–ö–ê –í–í–û–î–ê –û–¢–í–ï–¢–ê –° –°–ò–ù–•–†–û–ù–ò–ó–ê–¶–ò–ï–ô
  const handleAnswerChange = async (day, index, value) => {
    const newAnswers = {
      ...answers,
      [`${day}-${index}`]: value
    };

    // –ù–µ–º–µ–¥–ª–µ–Ω–Ω–æ –æ–±–Ω–æ–≤–ª—è–µ–º UI
    setAnswers(newAnswers);
    localStorage.setItem('userMathAnswers', JSON.stringify(newAnswers));

    try {
      const dayData = tests.find(d => d.day === day);
      if (dayData) {
        const task = dayData.tasks[index];
        const isCorrectAnswer = isCorrectAnswerCheck(day, index, task.answer, newAnswers);

        // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ç–≤–µ—Ç –Ω–∞ —Å–µ—Ä–≤–µ—Ä
        await userAPI.saveAnswer(day, index, value, isCorrectAnswer);

        // –û–±–Ω–æ–≤–ª—è–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å –¥–Ω—è
        const completedTasks = dayData.tasks.filter((t, i) => 
          isCorrectAnswerCheck(day, i, t.answer, newAnswers)
        ).length;

        await courseAPI.updateProgress(day, newAnswers, completedTasks);

        console.log('‚úÖ –û—Ç–≤–µ—Ç —Å–æ—Ö—Ä–∞–Ω–µ–Ω –∏ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞–Ω');
      }
    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –≤ –±–∞–∑—É:', error);
      // –û—Ç–≤–µ—Ç –æ—Å—Ç–∞–Ω–µ—Ç—Å—è –≤ localStorage –∏ –±—É–¥–µ—Ç —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞–Ω –ø—Ä–∏ —Å–ª–µ–¥—É—é—â–µ–π –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏
    }
  };

  // –ü—Ä–æ–≥—Ä–µ—Å—Å –¥–Ω—è
  const getDayProgress = (day, tasks) => {
    const correctCount = tasks.filter((t, i) =>
      isCorrectAnswerCheck(day, i, t.answer, answers)
    ).length;
    return Math.round((correctCount / tasks.length) * 100);
  };

  // –û–±—â–∏–π –ø—Ä–æ–≥—Ä–µ—Å—Å
  const getTotalProgress = () => {
    let totalCorrect = 0;
    let totalTasks = 0;

    tests.forEach(dayData => {
      dayData.tasks.forEach((task, index) => {
        totalTasks++;
        if (isCorrectAnswerCheck(dayData.day, index, task.answer, answers)) {
          totalCorrect++;
        }
      });
    });

    return totalTasks ? Math.round((totalCorrect / totalTasks) * 100) : 0;
  };

  // üü¢ –£–õ–£–ß–®–ï–ù–ù–´–ô –°–ë–†–û–° –ü–†–û–ì–†–ï–°–°–ê
  const resetProgress = async () => {
    try {
      // –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –Ω–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è
      const userTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator](day.taskCount, t)
      }));

      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ
      setTests(userTests);
      setAnswers({});

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º –ª–æ–∫–∞–ª—å–Ω–æ
      localStorage.setItem('userMathTests', JSON.stringify(userTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));

      // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º —Å —Å–µ—Ä–≤–µ—Ä–æ–º
      await courseAPI.resetProgress();

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º –Ω–æ–≤—ã–µ —Ç–µ—Å—Ç—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä
      for (const dayData of userTests) {
        await userAPI.saveTasks(dayData.day, dayData.tasks);
      }

      console.log('‚úÖ –ü—Ä–æ–≥—Ä–µ—Å—Å –ø–æ–ª–Ω–æ—Å—Ç—å—é —Å–±—Ä–æ—à–µ–Ω –∏ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞–Ω');
    } catch (error) {
      console.error('‚ùå Error resetting progress:', error);
      // –õ–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —É–∂–µ —Å–±—Ä–æ—à–µ–Ω—ã, —Å–µ—Ä–≤–µ—Ä —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ—Ç—Å—è –ø—Ä–∏ —Å–ª–µ–¥—É—é—â–µ–π –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏
    }
  };

  const handleLoginClick = () => {
    setShowLogin(true);
    setAuthError('');
  };

  const handleRegisterClick = () => {
    setShowRegister(true);
    setAuthError('');
  };

  const handleDayClick = (dayNumber) => {
    setCurrentDay(dayNumber);
    const element = document.getElementById(`day-${dayNumber}`);
    if (element) {
      const headerOffset = 340;
      const elementPosition = element.offsetTop;
      const offsetPosition = elementPosition - headerOffset;

      setTimeout(() => {
        window.scrollTo({
          top: Math.max(0, offsetPosition),
          behavior: 'smooth'
        });
      }, 50);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">{t('loading', "–ó–∞–≥—Ä—É–∑–∫–∞...")}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-200">
      <Header 
        user={user || { name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false }}
        progress={getTotalProgress()}
        onResetClick={() => setShowResetConfirm(true)}
        onSettingsClick={() => console.log("Settings clicked")}
        onLoginClick={handleLoginClick}
        onLogoutClick={handleLogout}
        grade="6"
      />

      {showLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <Login 
            onLogin={handleLogin} 
            onSwitchToRegister={() => { 
              setShowLogin(false); 
              setShowRegister(true); 
              setAuthError('');
            }}
            error={authError}
          />
        </div>
      )}

      {showRegister && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <Register 
            onRegister={handleRegister} 
            onSwitchToLogin={() => { 
