import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { authAPI, courseAPI, userAPI } from './services/api';
import { courseStructure } from './taskGenerators';
import * as taskGenerators from './taskGenerators';
import { useTranslation } from 'react-i18next';
import DayNavigation from './components/DayNavigation';

// Функция для применения переводов к заданиям
const applyTranslationsToTasks = (tasks, t) => {
  return tasks.map(task => ({
    ...task,
    question: task.translationKey ? t(task.translationKey, task.translationParams) : task.question,
    explanation: task.explanationKey ? t(task.explanationKey, task.explanationParams) : task.explanation,
    answerFormat: task.formatKey ? t(task.formatKey) : task.answerFormat
  }));
};

// 🟢 ФУНКЦИЯ ДЛЯ СИНХРОНИЗАЦИИ ПРОГРЕССА
const syncProgressWithServer = async (localAnswers, localTests) => {
  try {
    // Сохраняем тесты на сервер
    if (localTests && localTests.length > 0) {
      for (const dayData of localTests) {
        await userAPI.saveTasks(dayData.day, dayData.tasks);
      }
    }

    // Сохраняем ответы на сервер
    if (localAnswers && Object.keys(localAnswers).length > 0) {
      for (const [key, answer] of Object.entries(localAnswers)) {
        const [day, index] = key.split('-').map(Number);
        const dayData = localTests.find(d => d.day === day);
        if (dayData && dayData.tasks[index]) {
          const task = dayData.tasks[index];
          const isCorrectAnswer = isCorrectAnswerCheck(day, index, task.answer, localAnswers);
          await userAPI.saveAnswer(day, index, answer, isCorrectAnswer);
        }
      }
    }

    console.log('✅ Прогресс синхронизирован с сервером');
    return true;
  } catch (error) {
    console.error('❌ Ошибка синхронизации прогресса:', error);
    return false;
  }
};

// 🟢 ФУНКЦИЯ ДЛЯ ЗАГРУЗКИ ПРОГРЕССА С СЕРВЕРА
const loadProgressFromServer = async () => {
  try {
    const [progressResponse, answersResponse, tasksResponse] = await Promise.all([
      courseAPI.getProgress().catch(() => ({ data: {} })),
      userAPI.getAnswers('all').catch(() => ({ data: {} })),
      userAPI.getTasks('all').catch(() => ({ data: {} }))
    ]);

    const serverProgress = {
      answers: answersResponse.data || {},
      tests: tasksResponse.data || {},
      progress: progressResponse.data || {}
    };

    console.log('📥 Прогресс загружен с сервера:', serverProgress);
    return serverProgress;
  } catch (error) {
    console.error('❌ Ошибка загрузки прогресса с сервера:', error);
    return { answers: {}, tests: {}, progress: {} };
  }
};

// 🟢 ФУНКЦИЯ ПРОВЕРКИ ПРАВИЛЬНОСТИ ОТВЕТА (вынесена для переиспользования)
const isCorrectAnswerCheck = (day, index, correctAnswer, currentAnswers) => {
  const userAnswer = currentAnswers[`${day}-${index}`]?.toString().trim().toLowerCase();
  const correct = correctAnswer.toString().toLowerCase();

  if (!userAnswer) return false;

  if (correct.includes('%')) {
    const userNum = userAnswer.replace('%', '');
    const correctNum = correct.replace('%', '');
    return userNum === correctNum;
  }

  if (correct.includes('/')) {
    const [userA, userB] = userAnswer.split('/');
    const [correctA, correctB] = correct.split('/');
    return userA === correctA && userB === correctB;
  }

  return userAnswer === correct;
};

function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [showResetConfirm, setShowResetConfirm] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentDay, setCurrentDay] = useState(1);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [authError, setAuthError] = useState('');
  const [isSyncing, setIsSyncing] = useState(false);

  // 🟢 EFFECT ДЛЯ ОТСЛЕЖИВАНИЯ СКРОЛЛА
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // 🟢 ПРОВЕРКА АУТЕНТИФИКАЦИИ С СИНХРОНИЗАЦИЕЙ ПРОГРЕССА
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');
      const savedUser = localStorage.getItem('userData');

      if (token && savedUser) {
        const userData = JSON.parse(savedUser);
        setUser(userData);

        // 🟢 ЗАГРУЖАЕМ И СИНХРОНИЗИРУЕМ ПРОГРЕСС ПОСЛЕ ЛОГИНА
        if (userData.isLoggedIn) {
          await initializeUserProgress();
        }
      } else if (token) {
        try {
          const response = await authAPI.getMe();
          const userData = {
            ...response.data,
            isLoggedIn: true
          };
          setUser(userData);
          localStorage.setItem('userData', JSON.stringify(userData));

          // 🟢 ЗАГРУЖАЕМ И СИНХРОНИЗИРУЕМ ПРОГРЕСС
          await initializeUserProgress();
        } catch (error) {
          console.log('getMe failed, using fallback user');
          const userData = {
            name: 'Пользователь',
            email: 'user@example.com', 
            isLoggedIn: true
          };
          setUser(userData);
          localStorage.setItem('userData', JSON.stringify(userData));
        }
      } else {
        setUser({ name: t('header.guest', "Гость"), isLoggedIn: false });
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setUser({ name: t('header.guest', "Гость"), isLoggedIn: false });
    } finally {
      setLoading(false);
    }
  }, [t]);

  // 🟢 ИНИЦИАЛИЗАЦИЯ ПРОГРЕССА ПОЛЬЗОВАТЕЛЯ
  const initializeUserProgress = async () => {
    setIsSyncing(true);

    try {
      // 1. Загружаем прогресс с сервера
      const serverProgress = await loadProgressFromServer();

      // 2. Загружаем из localStorage
      const localTests = localStorage.getItem('userMathTests');
      const localAnswers = localStorage.getItem('userMathAnswers');

      let finalTests = [];
      let finalAnswers = {};

      // 3. ПРИОРИТЕТ СЕРВЕРА: если на сервере есть данные, используем их
      if (serverProgress.tests && Object.keys(serverProgress.tests).length > 0) {
        finalTests = Object.values(serverProgress.tests).map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        }));
        finalAnswers = serverProgress.answers;

        console.log('🔄 Используем данные с сервера');
      } 
      // 4. ЕСЛИ НА СЕРВЕРЕ НЕТ ДАННЫХ, но есть в localStorage - синхронизируем
      else if (localTests && localAnswers) {
        const parsedTests = JSON.parse(localTests);
        const parsedAnswers = JSON.parse(localAnswers);

        finalTests = parsedTests.map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        }));
        finalAnswers = parsedAnswers;

        // Синхронизируем локальные данные с сервером
        await syncProgressWithServer(parsedAnswers, parsedTests);
        console.log('🔄 Синхронизировали локальные данные с сервером');
      }
      // 5. ЕСЛИ ДАННЫХ НЕТ ВООБЩЕ - генерируем новые
      else {
        finalTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator](day.taskCount, t)
        }));

        // Сохраняем новые тесты на сервер
        for (const dayData of finalTests) {
          await userAPI.saveTasks(dayData.day, dayData.tasks);
        }
        console.log('🔄 Сгенерировали новые задания');
      }

      // 6. Сохраняем финальные данные в состояние и localStorage
      setTests(finalTests);
      setAnswers(finalAnswers);
      localStorage.setItem('userMathTests', JSON.stringify(finalTests));
      localStorage.setItem('userMathAnswers', JSON.stringify(finalAnswers));

    } catch (error) {
      console.error('❌ Ошибка инициализации прогресса:', error);

      // Фолбэк на localStorage
      const localTests = localStorage.getItem('userMathTests');
      const localAnswers = localStorage.getItem('userMathAnswers');

      if (localTests && localAnswers) {
        const parsedTests = JSON.parse(localTests);
        const parsedAnswers = JSON.parse(localAnswers);

        setTests(parsedTests.map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        })));
        setAnswers(parsedAnswers);
      } else {
        // Генерация новых заданий как последнее средство
        const newTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator](day.taskCount, t)
        }));
        setTests(newTests);
        localStorage.setItem('userMathTests', JSON.stringify(newTests));
      }
    } finally {
      setIsSyncing(false);
    }
  };

  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // 🟢 ОБРАБОТЧИК ВХОДА С СИНХРОНИЗАЦИЕЙ
  const handleLogin = async (formData) => {
    console.log('🔐 LOGIN ATTEMPT:', formData);
    setAuthError('');

    try {
      const response = await authAPI.login({ 
        email: formData.email, 
        password: formData.password 
      });
      console.log('✅ LOGIN SUCCESS:', response.data);

      localStorage.setItem('authToken', response.data.access_token);
      await checkAuth(); // Это вызовет initializeUserProgress
      setShowLogin(false);
    } catch (error) {
      console.error('❌ LOGIN ERROR:', error);
      const errorMessage = error.response?.data?.detail || error.message || 'Ошибка входа';
      setAuthError(errorMessage);
    }
  };

  // 🟢 ОБРАБОТЧИК РЕГИСТРАЦИИ
  const handleRegister = async (formData) => {
    console.log('📝 REGISTER ATTEMPT:', formData);
    setAuthError('');

    try {
      const response = await authAPI.register({ 
        email: formData.email,
        username: formData.username, 
        password: formData.password,
        grade: formData.grade
      });
      console.log('✅ REGISTER SUCCESS:', response.data);

      const loginResponse = await authAPI.login({
        email: formData.email,
        password: formData.password
      });
      localStorage.setItem('authToken', loginResponse.data.access_token);
      await checkAuth();
      setShowRegister(false);
    } catch (error) {
      console.error('❌ REGISTER ERROR:', error);
      const errorMessage = error.response?.data?.detail || error.message || 'Ошибка регистрации';
      setAuthError(errorMessage);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    setUser({ name: t('header.guest', "Гость"), isLoggedIn: false });
    setAnswers({});
    setTests([]);
    setAuthError('');
  };

  // 🟢 УЛУЧШЕННАЯ ОБРАБОТКА ВВОДА ОТВЕТА С СИНХРОНИЗАЦИЕЙ
  const handleAnswerChange = async (day, index, value) => {
    const newAnswers = {
      ...answers,
      [`${day}-${index}`]: value
    };

    // Немедленно обновляем UI
    setAnswers(newAnswers);
    localStorage.setItem('userMathAnswers', JSON.stringify(newAnswers));

    try {
      const dayData = tests.find(d => d.day === day);
      if (dayData) {
        const task = dayData.tasks[index];
        const isCorrectAnswer = isCorrectAnswerCheck(day, index, task.answer, newAnswers);

        // Сохраняем ответ на сервер
        await userAPI.saveAnswer(day, index, value, isCorrectAnswer);

        // Обновляем прогресс дня
        const completedTasks = dayData.tasks.filter((t, i) => 
          isCorrectAnswerCheck(day, i, t.answer, newAnswers)
        ).length;

        await courseAPI.updateProgress(day, newAnswers, completedTasks);

        console.log('✅ Ответ сохранен и синхронизирован');
      }
    } catch (error) {
      console.error('❌ Ошибка сохранения в базу:', error);
      // Ответ останется в localStorage и будет синхронизирован при следующей возможности
    }
  };

  // Прогресс дня
  const getDayProgress = (day, tasks) => {
    const correctCount = tasks.filter((t, i) =>
      isCorrectAnswerCheck(day, i, t.answer, answers)
    ).length;
    return Math.round((correctCount / tasks.length) * 100);
  };

  // Общий прогресс
  const getTotalProgress = () => {
    let totalCorrect = 0;
    let totalTasks = 0;

    tests.forEach(dayData => {
      dayData.tasks.forEach((task, index) => {
        totalTasks++;
        if (isCorrectAnswerCheck(dayData.day, index, task.answer, answers)) {
          totalCorrect++;
        }
      });
    });

    return totalTasks ? Math.round((totalCorrect / totalTasks) * 100) : 0;
  };

  // 🟢 УЛУЧШЕННЫЙ СБРОС ПРОГРЕССА
  const resetProgress = async () => {
    try {
      // Генерируем новые задания
      const userTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator](day.taskCount, t)
      }));

      // Обновляем состояние
      setTests(userTests);
      setAnswers({});

      // Сохраняем локально
      localStorage.setItem('userMathTests', JSON.stringify(userTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));

      // Синхронизируем с сервером
      await courseAPI.resetProgress();

      // Сохраняем новые тесты на сервер
      for (const dayData of userTests) {
        await userAPI.saveTasks(dayData.day, dayData.tasks);
      }

      console.log('✅ Прогресс полностью сброшен и синхронизирован');
    } catch (error) {
      console.error('❌ Error resetting progress:', error);
      // Локальные данные уже сброшены, сервер синхронизируется при следующей возможности
    }
  };

  const handleLoginClick = () => {
    setShowLogin(true);
    setAuthError('');
  };

  const handleRegisterClick = () => {
    setShowRegister(true);
    setAuthError('');
  };

  const handleDayClick = (dayNumber) => {
    setCurrentDay(dayNumber);
    const element = document.getElementById(`day-${dayNumber}`);
    if (element) {
      const headerOffset = 340;
      const elementPosition = element.offsetTop;
      const offsetPosition = elementPosition - headerOffset;

      setTimeout(() => {
        window.scrollTo({
          top: Math.max(0, offsetPosition),
          behavior: 'smooth'
        });
      }, 50);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">{t('loading', "Загрузка...")}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-200">
      <Header 
        user={user || { name: t('header.guest', "Гость"), isLoggedIn: false }}
        progress={getTotalProgress()}
        onResetClick={() => setShowResetConfirm(true)}
        onSettingsClick={() => console.log("Settings clicked")}
        onLoginClick={handleLoginClick}
        onLogoutClick={handleLogout}
        grade="6"
      />

      {showLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <Login 
            onLogin={handleLogin} 
            onSwitchToRegister={() => { 
              setShowLogin(false); 
              setShowRegister(true); 
              setAuthError('');
            }}
            error={authError}
          />
        </div>
      )}

      {showRegister && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <Register 
            onRegister={handleRegister} 
            onSwitchToLogin={() => { 
              setShowRegister(false); 
              setShowLogin(true); 
              setAuthError('');
            }}
            error={authError}
          />
        </div>
      )}

      {isSyncing && (
        <div className="fixed top-20 left-1/2 transform -translate-x-1/2 z-40 bg-blue-500 text-white px-4 py-2 rounded-lg shadow-lg">
          🔄 {t('syncing', "Синхронизация прогресса...")}
        </div>
      )}

      {user && user.id && tests.length > 0 && (
        <DayNavigation 
          days={tests.map(day => ({
            ...day,
            progress: getDayProgress(day.day, day.tasks)
          }))}
          currentDay={currentDay}
          onDayClick={handleDayClick}
        />
      )}

      {user && user.id ? (
        <div className="container mx-auto p-14 max-w-4xl">
          <div className="grid gap-16">
            {tests.map(dayData => {
              const dayProgress = getDayProgress(dayData.day, dayData.tasks);

              return (
                <div key={dayData.day} id={`day-${dayData.day}`} className="bg-white dark:bg-gray-800 rounded-xl shadow-md p-6">
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex-1 min-w-0">
                      <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-1">
                        {t('days.day', "День")} {dayData.day}
                      </h2>
                      <p className="text-lg text-gray-800 dark:text-gray-200 font-medium">
                        {t(dayData.titleKey)}
                      </p>
                    </div>

                    <div className="flex flex-col items-end space-y-1 flex-shrink-0">
                      <div className="flex items-center space-x-2">
                        <span className="text-sm font-semibold text-gray-600 dark:text-gray-400">
                          {dayProgress}%
                        </span>
                        <div className="w-20 bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                          <div 
                            className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                            style={{ width: `${dayProgress}%` }}
                          />
                        </div>
                      </div>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        {dayData.tasks.length} {t('days.tasks', "заданий")}
                      </p>
                    </div>
                  </div>

                  <div className="space-y-3">
                    {dayData.tasks.map((task, index) => {
                      const isAnswerCorrect = isCorrectAnswerCheck(dayData.day, index, task.answer, answers);
                      const hasAnswer = answers[`${dayData.day}-${index}`];

                      return (
                        <div key={index} className={`p-4 rounded-lg ${
                          hasAnswer 
                            ? isAnswerCorrect 
                              ? 'bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800'
                              : 'bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800'
                            : 'bg-gray-50 dark:bg-gray-700'
                        }`}>
                          <div className="flex items-center space-x-3">
                            <span className="flex-1 text-gray-800 dark:text-gray-200">
                              <strong>{t('tasks.task', "Задача")} {index + 1}:</strong> {task.question}
                            </span>

                            <div className="flex items-center space-x-2">
                              <input
                                type="text"
                                placeholder={t('answer', "Ответ")}
                                value={answers[`${dayData.day}-${index}`] || ""}
                                onChange={e => handleAnswerChange(dayData.day, index, e.target.value)}
                                className="w-24 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-800 text-gray-900 dark:text-white text-center"
                              />
                              {hasAnswer && (
                                isAnswerCorrect ? (
                                  <span className="text-green-500 text-xl" title={t('correct', "Правильно!")}>✅</span>
                                ) : (
                                  <span className="text-red-500 text-xl" title={t('try_again', "Попробуйте еще раз")}>❌</span>
                                )
                              )}
                            </div>
                          </div>

                          {task.answerFormat && (
                            <div className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                              💡 {t('answerFormat.hint', "Формат ответа")}: {task.answerFormat}
                            </div>
                          )}

                          {hasAnswer && !isAnswerCorrect && (
                            <div className="mt-2 p-2 bg-blue-50 dark:bg-blue-900/20 rounded-md">
                              <p className="text-sm text-blue-800 dark:text-blue-200">
                                💡 {task.explanation}
                              </p>
                            </div>
                          )}
                        </div>
                      );
                    })}
                  </div>

                  <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-600">
                    <a
                      href={dayData.theoryLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 dark:text-blue-400 hover:underline text-sm flex items-center"
                    >
                      📖 {t('days.theory', "Теоретические материалы")} "{t(dayData.titleKey)}"
                    </a>
                  </div>
                </div>
              );
            })}
          </div>

          <div className="mt-8 text-center text-gray-500 dark:text-gray-400 text-sm">
            <p>{t('footer.course_description', "Курс для закрепления знаний по математике за 6 класс")}</p>
            <p>© 2025 Math Academy | {t('footer.generated', "Задания генерируются случайно")}</p>
          </div>
        </div>
      ) : (
        <div className="min-h-screen flex items-center justify-center px-4">
          <div className="text-center max-w-2xl mx-auto">
            <div className="mb-8 flex justify-center">
              <div className="w-32 h-32 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white text-6xl">
                📚
              </div>
            </div>

            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-6">
              {t('auth.welcome', "Добро пожаловать в Math Academy!")}
            </h1>

            <p className="text-xl text-gray-600 dark:text-gray-400 mb-8 max-w-md mx-auto leading-relaxed">
              {t('auth.prompt', "Войдите или зарегистрируйтесь чтобы начать обучение")}
            </p>

            <div className="flex flex-col sm:flex-row gap-4 justify-center mb-12">
              <button
                onClick={handleLoginClick}
                className="bg-blue-500 hover:bg-blue-600 text-white px-8 py-3 rounded-lg font-medium text-lg transition-colors shadow-lg"
              >
                {t('header.login', "Войти")}
              </button>
              <button
                onClick={handleRegisterClick}
                className="bg-green-500 hover:bg-green-600 text-white px-8 py-3 rounded-lg font-medium text-lg transition-colors shadow-lg"
              >
                {t('header.register', "Регистрация")}
              </button>
            </div>

            <div className="border-t border-gray-200 dark:border-gray-700 pt-8">
              <p className="text-lg text-gray-500 dark:text-gray-400 italic">
                {t('slogan', "От математических основ к образовательной экосистеме! 🚀")}
              </p>
            </div>
          </div>
        </div>
      )}

      {showResetConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 rounded-lg p-6 m-4 max-w-md w-full">
            <h3 className="text-lg font-bold text-gray-900 dark:text-white mb-2">
              🔄 {t('reset.new_tasks', "Новые задания")}
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              {t('reset.confirmation', "Вы уверены, что хотите сгенерировать новые задания? Весь текущий прогресс будет сброшен.")}
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowResetConfirm(false)}
                className="flex-1 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
              >
                {t('reset.cancel', "Отмена")}
              </button>
              <button
                onClick={() => {
                  resetProgress();
                  setShowResetConfirm(false);
                }}
                className="flex-1 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-lg transition-colors"
              >
                {t('reset.generate', "Сгенерировать")}
              </button>
            </div>
          </div>
        </div>
      )}

      {showScrollTop && (
        <button 
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          className="fixed bottom-6 right-6 z-50 bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-500 rounded-full w-10 h-10 flex items-center justify-center shadow-lg hover:bg-gray-50 dark:hover:bg-gray-700 transition-all text-gray-600 dark:text-gray-300 hover:text-gray-800 dark:hover:text-gray-100"
          aria-label="Наверх"
        >
          ↑
        </button>
      )}
    </div>
  );
}

export default App;
