              >
                {t('reset.cancel', "–û—Ç–º–µ–Ω–∞")}
              </button>
              <button
                onClick={() => {
                  resetProgress();
                  setShowResetConfirm(false);
                }}
                className="flex-1 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-lg transition-colors"
              >
                {t('reset.generate', "–°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å")}
