import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { authAPI, courseAPI, userAPI } from './services/api';
import { courseStructure } from './taskGenerators';
import * as taskGenerators from './taskGenerators';
import { useTranslation } from 'react-i18next';
import DayNavigation from './components/DayNavigation';

// ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐ«Ð™ Ð”Ð•Ð¢Ð•ÐšÐ¢ÐžÐ  Ð¡Ð‘Ð ÐžÐ¡Ð ÐšÐ­Ð¨Ð
const detectCacheClear = () => {
  const cacheFlag = sessionStorage.getItem('cacheWasCleared');
  const hasAuthToken = localStorage.getItem('authToken');
  const hasLocalData = localStorage.getItem('userMathTests') && localStorage.getItem('userMathAnswers');

  // Ð•ÑÐ»Ð¸ ÐµÑÑ‚ÑŒ Ñ‚Ð¾ÐºÐµÐ½ Ð½Ð¾ Ð½ÐµÑ‚ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ… - Ð·Ð½Ð°Ñ‡Ð¸Ñ‚ ÐºÐµÑˆ ÑÐ±Ñ€Ð¾ÑˆÐµÐ½
  if (hasAuthToken && !hasLocalData) {
    console.log('ðŸš¨ ÐžÐ‘ÐÐÐ Ð£Ð–Ð•Ð Ð¡Ð‘Ð ÐžÐ¡ ÐšÐ­Ð¨Ð: ÐµÑÑ‚ÑŒ Ñ‚Ð¾ÐºÐµÐ½ Ð½Ð¾ Ð½ÐµÑ‚ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ…');
    sessionStorage.setItem('cacheWasCleared', 'true');
    return true;
  }

  return cacheFlag === 'true';
};

// Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´Ð¾Ð² Ðº Ð·Ð°Ð´Ð°Ð½Ð¸ÑÐ¼
const applyTranslationsToTasks = (tasks, t) => {
  if (!tasks || !Array.isArray(tasks)) return [];

  return tasks.map(task => ({
    ...task,
    question: task.translationKey ? t(task.translationKey, task.translationParams) : task.question,
    explanation: task.explanationKey ? t(task.explanationKey, task.explanationParams) : task.explanation,
    answerFormat: task.formatKey ? t(task.formatKey) : task.answerFormat
  }));
};

// ðŸŸ¢ Ð¤Ð£ÐÐšÐ¦Ð˜Ð¯ ÐŸÐ ÐžÐ’Ð•Ð ÐšÐ˜ ÐŸÐ ÐÐ’Ð˜Ð›Ð¬ÐÐžÐ¡Ð¢Ð˜ ÐžÐ¢Ð’Ð•Ð¢Ð
const isCorrectAnswerCheck = (day, index, correctAnswer, currentAnswers) => {
  if (!currentAnswers) return false;

  const userAnswer = currentAnswers[`${day}-${index}`]?.toString().trim().toLowerCase();
  const correct = correctAnswer?.toString().toLowerCase();

  if (!userAnswer || !correct) return false;

  if (correct.includes('%')) {
    const userNum = userAnswer.replace('%', '');
    const correctNum = correct.replace('%', '');
    return userNum === correctNum;
  }

  if (correct.includes('/')) {
    const [userA, userB] = userAnswer.split('/');
    const [correctA, correctB] = correct.split('/');
    return userA === correctA && userB === correctB;
  }

  return userAnswer === correct;
};

function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [showResetConfirm, setShowResetConfirm] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentDay, setCurrentDay] = useState(1);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [authError, setAuthError] = useState('');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [recoveryAttempted, setRecoveryAttempted] = useState(false);

  // ðŸŸ¢ EFFECT Ð”Ð›Ð¯ ÐžÐ¢Ð¡Ð›Ð•Ð–Ð˜Ð’ÐÐÐ˜Ð¯ Ð¡ÐšÐ ÐžÐ›Ð›Ð
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // ðŸŸ¢ EFFECT Ð”Ð›Ð¯ ÐŸÐ ÐžÐ’Ð•Ð ÐšÐ˜ Ð¦Ð•Ð›ÐžÐ¡Ð¢ÐÐžÐ¡Ð¢Ð˜ Ð”ÐÐÐÐ«Ð¥ ÐŸÐ Ð˜ Ð—ÐÐ“Ð Ð£Ð—ÐšÐ•
  useEffect(() => {
    const checkDataIntegrity = () => {
      const hasAuthToken = localStorage.getItem('authToken');
      const hasTests = localStorage.getItem('userMathTests');
      const hasAnswers = localStorage.getItem('userMathAnswers');

      // Ð•ÑÐ»Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½ Ð½Ð¾ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð¾Ð²Ñ€ÐµÐ¶Ð´ÐµÐ½Ñ‹/Ð¾Ñ‚ÑÑƒÑ‚ÑÑ‚Ð²ÑƒÑŽÑ‚
      if (hasAuthToken && (!hasTests || !hasAnswers)) {
        console.log('ðŸš¨ ÐžÐ‘ÐÐÐ Ð£Ð–Ð•ÐÐ ÐŸÐžÐ’Ð Ð•Ð–Ð”Ð•ÐÐÐÐ¯ Ð¡Ð•Ð¡Ð¡Ð˜Ð¯ - Ð·Ð°Ð¿ÑƒÑÐº Ð²Ð¾ÑÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ');
        sessionStorage.setItem('cacheWasCleared', 'true');
        setRecoveryAttempted(false);
      }
    };

    checkDataIntegrity();
  }, []);

  // ðŸŸ¢ ÐÐÐ”Ð•Ð–ÐÐÐ¯ ÐŸÐ ÐžÐ’Ð•Ð ÐšÐ ÐÐ£Ð¢Ð•ÐÐ¢Ð˜Ð¤Ð˜ÐšÐÐ¦Ð˜Ð˜
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');

      if (!token) {
        setUser({ name: t('header.guest', "Ð“Ð¾ÑÑ‚ÑŒ"), isLoggedIn: false });
        setLoading(false);
        return;
      }

      try {
        const response = await authAPI.getMe();
        const userData = {
          ...response.data,
          isLoggedIn: true
        };
        setUser(userData);
        localStorage.setItem('userData', JSON.stringify(userData));
      } catch (error) {
        console.log('getMe failed, checking localStorage for user data');

        const savedUser = localStorage.getItem('userData');
        if (savedUser) {
          const userData = JSON.parse(savedUser);
          setUser({ ...userData, isLoggedIn: true });
        } else {
          const tempUser = {
            id: Date.now(),
            name: 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ',
            email: 'user@example.com',
            isLoggedIn: true
          };
          setUser(tempUser);
          localStorage.setItem('userData', JSON.stringify(tempUser));
        }
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setUser({ name: t('header.guest', "Ð“Ð¾ÑÑ‚ÑŒ"), isLoggedIn: false });
    } finally {
      setLoading(false);
    }
  }, [t]);

  // ðŸŸ¢ Ð—ÐÐ“Ð Ð£Ð—ÐšÐ ÐŸÐ Ð˜ Ð¡Ð¢ÐÐ Ð¢Ð•
  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // ðŸŸ¢ Ð£Ð›Ð£Ð§Ð¨Ð•ÐÐÐÐ¯ Ð¤Ð£ÐÐšÐ¦Ð˜Ð¯ Ð’ÐžÐ¡Ð¡Ð¢ÐÐÐžÐ’Ð›Ð•ÐÐ˜Ð¯ Ð˜Ð— Ð‘ÐÐ—Ð« Ð”ÐÐÐÐ«Ð¥
  const recoverFromDatabase = async () => {
    if (recoveryAttempted) return;

    const hasAuthToken = localStorage.getItem('authToken');
    const hasLocalTests = localStorage.getItem('userMathTests');
    const hasLocalAnswers = localStorage.getItem('userMathAnswers');

    console.log('ðŸ” Ð¡Ð¢ÐÐ¢Ð£Ð¡ Ð’ÐžÐ¡Ð¡Ð¢ÐÐÐžÐ’Ð›Ð•ÐÐ˜Ð¯:', {
      hasAuthToken: !!hasAuthToken,
      hasLocalTests: !!hasLocalTests, 
      hasLocalAnswers: !!hasLocalAnswers,
      recoveryAttempted
    });

    const wasCacheCleared = detectCacheClear();

    // ðŸŸ¢ Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐ˜Ð•: Ð’Ð¾ÑÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð¸Ð· Ð‘Ð” Ð²ÑÐµÐ³Ð´Ð° ÐºÐ¾Ð³Ð´Ð° ÐµÑÑ‚ÑŒ Ñ‚Ð¾ÐºÐµÐ½, Ð´Ð°Ð¶Ðµ ÐµÑÐ»Ð¸ ÐµÑÑ‚ÑŒ Ð»Ð¾ÐºÐ°Ð»ÑŒÐ½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ
    if (hasAuthToken) {
      console.log('ðŸš¨ Ð’ÐžÐ¡Ð¡Ð¢ÐÐÐžÐ’Ð›Ð•ÐÐ˜Ð• Ð˜Ð— Ð‘Ð”: ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½!');
      setRecoveryAttempted(true);

      if (wasCacheCleared) {
        sessionStorage.removeItem('cacheWasCleared');
      }

      try {
        console.log('ðŸ“š Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð¸Ð· Ð‘Ð”...');
        const tasksResponse = await userAPI.getAllTasks();
        const dbTasks = tasksResponse.data || {};

        console.log('âœ… Ð—Ð°Ð´Ð°Ð½Ð¸Ñ Ð¸Ð· Ð‘Ð”:', Object.keys(dbTasks).length, 'Ð´Ð½ÐµÐ¹');

        console.log('ðŸ“¥ Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚Ñ‹ Ð¸Ð· Ð‘Ð”...');
        const answersResponse = await userAPI.getAnswers();
        const dbAnswers = answersResponse.data || {};

        console.log('âœ… ÐžÑ‚Ð²ÐµÑ‚Ñ‹ Ð¸Ð· Ð‘Ð”:', Object.keys(dbAnswers).length, 'Ð·Ð°Ð¿Ð¸ÑÐµÐ¹');

        let restoredTests = [];
        let shouldGenerateNewTasks = false;

        if (Object.keys(dbTasks).length > 0) {
          console.log('ðŸŽ¯ Ð’ÐžÐ¡Ð¡Ð¢ÐÐÐÐ’Ð›Ð˜Ð’ÐÐ•Ðœ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð½Ñ‹Ðµ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð¸Ð· Ð‘Ð”');

          restoredTests = Object.keys(dbTasks).map(dayStr => {
            const day = parseInt(dayStr);
            const dayTasks = dbTasks[dayStr];

            const dayInfo = courseStructure.find(d => d.day === day) || {
              titleKey: `days.day${day}`,
              theoryLink: null
            };

            return {
              day: day,
              titleKey: dayInfo.titleKey,
              theoryLink: dayInfo.theoryLink,
              tasks: dayTasks.map(task => ({
                question: task.question,
                answer: task.answer,
                explanation: task.explanation,
                answerFormat: task.answerFormat,
                translationKey: task.translation_key,
                translationParams: task.translation_params,
                ...(task.task_data && typeof task.task_data === 'object' ? task.task_data : {})
              }))
            };
          }).filter(day => day.tasks.length > 0);

          console.log(`âœ… Ð’Ð¾ÑÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½Ð¾ ${restoredTests.length} Ð´Ð½ÐµÐ¹ Ñ Ð·Ð°Ð´Ð°Ð½Ð¸ÑÐ¼Ð¸ Ð¸Ð· Ð‘Ð”`);

        } else {
          console.log('ðŸ†• Ð¡ÐžÐ¥Ð ÐÐÐ•ÐÐÐ«Ð¥ Ð—ÐÐ”ÐÐÐ˜Ð™ ÐÐ•Ð¢ - Ð³ÐµÐ½ÐµÑ€Ð¸Ñ€ÑƒÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ðµ');
          shouldGenerateNewTasks = true;
          restoredTests = courseStructure.map(day => ({
            day: day.day,
            titleKey: day.titleKey,
            theoryLink: day.theoryLink,
            tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
          })).filter(day => day.tasks.length > 0);

          console.log(`âœ… Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¾ ${restoredTests.length} Ð´Ð½ÐµÐ¹ Ñ Ð½Ð¾Ð²Ñ‹Ð¼Ð¸ Ð·Ð°Ð´Ð°Ð½Ð¸ÑÐ¼Ð¸`);
        }

        // ðŸŸ¢ Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐ˜Ð•: Ð’Ð¾ÑÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð’Ð¡Ð• Ð¾Ñ‚Ð²ÐµÑ‚Ñ‹ Ð¸Ð· Ð‘Ð”, Ð´Ð°Ð¶Ðµ ÐµÑÐ»Ð¸ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð½Ð¾Ð²Ñ‹Ðµ
        const restoredAnswers = {};

        if (Object.keys(dbAnswers).length > 0) {
          console.log('ðŸ”„ Ð’Ð¾ÑÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚Ñ‹ Ð¸Ð· Ð‘Ð”...');

          Object.keys(dbAnswers).forEach(key => {
            const [day, index] = key.split('-').map(Number);

            // Ð•ÑÐ»Ð¸ Ñƒ Ð½Ð°Ñ ÐµÑÑ‚ÑŒ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð´Ð»Ñ ÑÑ‚Ð¾Ð³Ð¾ Ð´Ð½Ñ Ð¸ Ð¸Ð½Ð´ÐµÐºÑÐ° - Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚
            const dayData = restoredTests.find(d => d.day === day);
            if (dayData && dayData.tasks && dayData.tasks[index]) {
              restoredAnswers[key] = dbAnswers[key];
            } 
            // ðŸŸ¢ Ð’ÐÐ–ÐÐžÐ• Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐ˜Ð•: Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚ Ð´Ð°Ð¶Ðµ ÐµÑÐ»Ð¸ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ Ð½Ðµ ÑÐ¾Ð²Ð¿Ð°Ð´Ð°ÑŽÑ‚
            else {
              console.log(`ðŸ“¦ Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚ Ð´Ð»Ñ Ð±ÑƒÐ´ÑƒÑ‰Ð¸Ñ… Ð·Ð°Ð´Ð°Ð½Ð¸Ð¹: ${key}`);
              restoredAnswers[key] = dbAnswers[key];
            }
          });

          console.log(`âœ… Ð’Ð¾ÑÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½Ð¾ Ð¾Ñ‚Ð²ÐµÑ‚Ð¾Ð²: ${Object.keys(restoredAnswers).length}`);
        }

        // ðŸŸ¢ Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐ˜Ð•: Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ðµ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð² Ð‘Ð” ÐµÑÐ»Ð¸ Ð¸Ñ… Ð½Ðµ Ð±Ñ‹Ð»Ð¾
        if (shouldGenerateNewTasks) {
          console.log('ðŸ’¾ Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ ÑÐ³ÐµÐ½ÐµÑ€Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ðµ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ Ð² Ð‘Ð”...');
          for (const dayData of restoredTests) {
            try {
              await userAPI.saveTasks(dayData.day, dayData.tasks);
              console.log(`âœ… Ð”ÐµÐ½ÑŒ ${dayData.day} ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½ Ð² Ð‘Ð”`);
            } catch (error) {
              console.error(`âŒ ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ñ Ð´Ð½Ñ ${dayData.day}:`, error);
            }
          }
        }

        // ðŸŸ¢ Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐ˜Ð•: Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ñ Ð¿Ñ€Ð°Ð²Ð¸Ð»ÑŒÐ½Ð¾Ð¹ Ð¿Ð¾ÑÐ»ÐµÐ´Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒÐ½Ð¾ÑÑ‚ÑŒÑŽ
        setTests(restoredTests);

        // ÐÐµÐ±Ð¾Ð»ÑŒÑˆÐ°Ñ Ð·Ð°Ð´ÐµÑ€Ð¶ÐºÐ° Ð´Ð»Ñ Ð³Ð°Ñ€Ð°Ð½Ñ‚Ð¸Ð¸ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ
        setTimeout(() => {
          setAnswers(restoredAnswers);
          localStorage.setItem('userMathTests', JSON.stringify(restoredTests));
          localStorage.setItem('userMathAnswers', JSON.stringify(restoredAnswers));
