import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { authAPI, courseAPI, userAPI } from './services/api';
import { courseStructure } from './taskGenerators';
import * as taskGenerators from './taskGenerators';
import { useTranslation } from 'react-i18next';
import DayNavigation from './components/DayNavigation';

// Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´Ð¾Ð² Ðº Ð·Ð°Ð´Ð°Ð½Ð¸ÑÐ¼
const applyTranslationsToTasks = (tasks, t) => {
  return tasks.map(task => ({
    ...task,
    question: task.translationKey ? t(task.translationKey, task.translationParams) : task.question,
    explanation: task.explanationKey ? t(task.explanationKey, task.explanationParams) : task.explanation,
    answerFormat: task.formatKey ? t(task.formatKey) : task.answerFormat
  }));
};

function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [showResetConfirm, setShowResetConfirm] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentDay, setCurrentDay] = useState(1);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [authError, setAuthError] = useState(''); // ðŸŸ¢ Ð”ÐžÐ‘ÐÐ’Ð›Ð•ÐÐž: Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸

  // ðŸŸ¢ EFFECT Ð”Ð›Ð¯ ÐžÐ¢Ð¡Ð›Ð•Ð–Ð˜Ð’ÐÐÐ˜Ð¯ Ð¡ÐšÐ ÐžÐ›Ð›Ð
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');
      const savedUser = localStorage.getItem('userData');

      if (token && savedUser) {
        setUser(JSON.parse(savedUser));
      } else if (token) {
        try {
          const response = await authAPI.getMe();
          const userData = {
            ...response.data,
            isLoggedIn: true
          };
          setUser(userData);
          localStorage.setItem('userData', JSON.stringify(userData));
        } catch (error) {
          console.log('getMe failed, using fallback user');
          const userData = {
            name: 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ',
            email: 'user@example.com', 
            isLoggedIn: true
          };
          setUser(userData);
          localStorage.setItem('userData', JSON.stringify(userData));
        }
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setUser({ name: t('header.guest', "Ð“Ð¾ÑÑ‚ÑŒ"), isLoggedIn: false });
    } finally {
      setLoading(false);
    }
  }, [t]);

  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // ðŸŸ¢ ÐžÐ‘Ð ÐÐ‘ÐžÐ¢Ð§Ð˜ÐšÐ˜ Ð¡ API Ð’Ð«Ð—ÐžÐ’ÐÐœÐ˜ Ð˜ ÐžÐ‘Ð ÐÐ‘ÐžÐ¢ÐšÐžÐ™ ÐžÐ¨Ð˜Ð‘ÐžÐš
  const handleLogin = async (formData) => {
    console.log('ðŸ” LOGIN ATTEMPT:', formData);
    setAuthError(''); // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ Ð¾ÑˆÐ¸Ð±ÐºÑƒ

    try {
      const response = await authAPI.login({ 
        email: formData.email, 
        password: formData.password 
      });
      console.log('âœ… LOGIN SUCCESS:', response.data);

      localStorage.setItem('authToken', response.data.access_token);

      // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð½Ð° Ð¾ÑÐ½Ð¾Ð²Ðµ Ð´Ð°Ð½Ð½Ñ‹Ñ… Ñ„Ð¾Ñ€Ð¼Ñ‹
      const userData = {
        name: formData.email.split('@')[0],
        email: formData.email,
        isLoggedIn: true
      };
      setUser(userData);
      localStorage.setItem('userData', JSON.stringify(userData));

      setShowLogin(false);
    } catch (error) {
      console.error('âŒ LOGIN ERROR:', error);
      const errorMessage = error.response?.data?.detail || error.message || 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð²Ñ…Ð¾Ð´Ð°';
      setAuthError(errorMessage);
      console.error('Error details:', error.response?.data);
    }
  };

  const handleRegister = async (formData) => {
    console.log('ðŸ“ REGISTER ATTEMPT:', formData);
    setAuthError(''); // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ Ð¾ÑˆÐ¸Ð±ÐºÑƒ

    try {
      const response = await authAPI.register({ 
        email: formData.email,
        username: formData.username, 
        password: formData.password,
        grade: formData.grade
      });
      console.log('âœ… REGISTER SUCCESS:', response.data);

      // ÐŸÐ¾ÑÐ»Ðµ Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð°Ñ†Ð¸Ð¸ Ð°Ð²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸ Ð»Ð¾Ð³Ð¸Ð½Ð¸Ð¼
      const loginResponse = await authAPI.login({
        email: formData.email,
        password: formData.password
      });
      localStorage.setItem('authToken', loginResponse.data.access_token);

      const userData = {
        name: formData.username,
        email: formData.email,
        isLoggedIn: true
      };
      setUser(userData);
      localStorage.setItem('userData', JSON.stringify(userData));

      setShowRegister(false);
    } catch (error) {
      console.error('âŒ REGISTER ERROR:', error);
      const errorMessage = error.response?.data?.detail || error.message || 'ÐžÑˆÐ¸Ð±ÐºÐ° Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð°Ñ†Ð¸Ð¸';
      setAuthError(errorMessage);
      console.error('Error details:', error.response?.data);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    setUser({ name: t('header.guest', "Ð“Ð¾ÑÑ‚ÑŒ"), isLoggedIn: false });
    setAnswers({});
    setTests([]);
    setAuthError('');
  };

  // Ð“ÐµÐ½ÐµÑ€Ð°Ñ†Ð¸Ñ Ð¸Ð»Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð·Ð°Ð´Ð°Ð½Ð¸Ð¹
  const loadUserData = useCallback(async () => {
    try {
      // 1. Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚Ñ‹ Ð¸Ð· localStorage
      const savedAnswers = localStorage.getItem('userMathAnswers');
      if (savedAnswers) {
        const parsedAnswers = JSON.parse(savedAnswers);
        setAnswers(parsedAnswers);
      }

      // 2. Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑ Ð¸ Ð¾Ñ‚Ð²ÐµÑ‚Ñ‹ Ð¸Ð· API
      let apiAnswers = {};
      let userProgressData = {};

      try {
        const [progressResponse, answersResponse] = await Promise.all([
          courseAPI.getProgress(),
          userAPI.getAnswers('all')
        ]);

        if (progressResponse && progressResponse.data) {
          userProgressData = progressResponse.data;
          console.log('Ð—Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½ Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ:', userProgressData);
        }

        apiAnswers = answersResponse.data || {};

        if (Object.keys(apiAnswers).length > 0) {
          setAnswers(apiAnswers);
          localStorage.setItem('userMathAnswers', JSON.stringify(apiAnswers));
        }
      } catch (error) {
        console.log('API Ð½ÐµÐ´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ð¾, Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ localStorage');
      }

      // 3. Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¸Ð»Ð¸ Ð³ÐµÐ½ÐµÑ€Ð¸Ñ€ÑƒÐµÐ¼ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ
      let userTests = [];
      const savedTests = localStorage.getItem('userMathTests');

      if (savedTests) {
        const rawTests = JSON.parse(savedTests);
        userTests = rawTests.map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        }));
      } else {
        for (const day of courseStructure) {
          const generatorFn = taskGenerators[day.generator];
          if (generatorFn) {
            const tasks = generatorFn(day.taskCount, t);
            userTests.push({
              day: day.day,
              titleKey: day.titleKey,
              theoryLink: day.theoryLink,
              tasks: tasks
            });
          }
        }
        localStorage.setItem('userMathTests', JSON.stringify(userTests));
      }

      setTests(userTests);

    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð´Ð°Ð½Ð½Ñ‹Ñ…:', error);

      const savedTests = localStorage.getItem('userMathTests');
      const savedAnswers = localStorage.getItem('userMathAnswers');

      if (savedTests) {
        setTests(JSON.parse(savedTests));
      } else {
        const userTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator](day.taskCount, t)
        }));
        setTests(userTests);
      }

      if (savedAnswers) {
        setAnswers(JSON.parse(savedAnswers));
      }
    }
  }, [t]);

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¿Ñ€Ð¸ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  useEffect(() => {
    if (user && user.id) {
      loadUserData();
    }
  }, [user, loadUserData]);

  // ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ð¿Ñ€Ð¸ ÑÐ¼ÐµÐ½Ðµ ÑÐ·Ñ‹ÐºÐ°
  useEffect(() => {
    if (user && user.id) {
      loadUserData();
    }
  }, [i18n.language, user, loadUserData]);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð²Ð²Ð¾Ð´Ð° Ð¾Ñ‚Ð²ÐµÑ‚Ð°
  const handleAnswerChange = async (day, index, value) => {
    const newAnswers = {
      ...answers,
      [`${day}-${index}`]: value
    };

    setAnswers(newAnswers);
    localStorage.setItem('userMathAnswers', JSON.stringify(newAnswers));

    try {
      const dayData = tests.find(d => d.day === day);
      if (dayData) {
        const task = dayData.tasks[index];
        const isCorrectAnswer = isCorrect(day, index, task.answer, newAnswers);

        await userAPI.saveAnswer(day, index, value, isCorrectAnswer);

        const completedTasks = dayData.tasks.filter((t, i) => 
          isCorrect(day, i, t.answer, newAnswers)
        ).length;

        await courseAPI.updateProgress(day, newAnswers, completedTasks);
      }
    } catch (error) {
