    }
  }, [t]);

  // ðŸŸ¢ Ð—ÐÐ“Ð Ð£Ð—ÐšÐ ÐŸÐ Ð˜ Ð¡Ð¢ÐÐ Ð¢Ð•
  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // ðŸŸ¢ ÐÐÐ”Ð•Ð–ÐÐÐ¯ Ð—ÐÐ“Ð Ð£Ð—ÐšÐ Ð”ÐÐÐÐ«Ð¥
  const loadUserData = useCallback(async () => {
    if (dataLoaded) return;

    try {
      // 1. Ð¡Ð½Ð°Ñ‡Ð°Ð»Ð° Ð·Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¸Ð· localStorage (Ð¼Ð³Ð½Ð¾Ð²ÐµÐ½Ð½Ð¾)
      const savedTests = localStorage.getItem('userMathTests');
      const savedAnswers = localStorage.getItem('userMathAnswers');

      let finalTests = [];
      let finalAnswers = {};

      if (savedTests && savedAnswers) {
        try {
          const parsedTests = JSON.parse(savedTests);
          const parsedAnswers = JSON.parse(savedAnswers);

          finalTests = parsedTests.map(dayData => ({
            ...dayData,
            tasks: applyTranslationsToTasks(dayData.tasks, t)
          }));
          finalAnswers = parsedAnswers;
        } catch (parseError) {
          console.error('Error parsing localStorage data:', parseError);
          // Ð•ÑÐ»Ð¸ Ð¾ÑˆÐ¸Ð±ÐºÐ° Ð¿Ð°Ñ€ÑÐ¸Ð½Ð³Ð° - Ð³ÐµÐ½ÐµÑ€Ð¸Ñ€ÑƒÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ
          throw new Error('Invalid localStorage data');
        }
      } else {
        // 2. Ð“ÐµÐ½ÐµÑ€Ð¸Ñ€ÑƒÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ðµ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ
        finalTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
        })).filter(day => day.tasks.length > 0);

        finalAnswers = {};
      }

      // 3. Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ
      setTests(finalTests);
      setAnswers(finalAnswers);

      // 4. Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð² localStorage
      localStorage.setItem('userMathTests', JSON.stringify(finalTests));
      localStorage.setItem('userMathAnswers', JSON.stringify(finalAnswers));

      setDataLoaded(true);

      // 5. Ð¤ÐžÐÐ¾Ð²Ð°Ñ ÑÐ¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð°Ñ†Ð¸Ñ Ñ ÑÐµÑ€Ð²ÐµÑ€Ð¾Ð¼ Ð´Ð»Ñ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½Ð½Ñ‹Ñ… Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÐµÐ¹
      if (user && user.isLoggedIn) {
        setTimeout(async () => {
          try {
            console.log('ðŸ”„ Starting background sync with server...');

            const [serverAnswers, serverProgress] = await Promise.all([
              userAPI.getAnswers('all').catch(() => ({ data: {} })),
              courseAPI.getProgress().catch(() => ({ data: {} }))
            ]);

            // Ð•ÑÐ»Ð¸ Ð½Ð° ÑÐµÑ€Ð²ÐµÑ€Ðµ ÐµÑÑ‚ÑŒ Ð¾Ñ‚Ð²ÐµÑ‚Ñ‹ - Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ Ð¸Ñ…
            if (serverAnswers.data && Object.keys(serverAnswers.data).length > 0) {
              setAnswers(serverAnswers.data);
              localStorage.setItem('userMathAnswers', JSON.stringify(serverAnswers.data));
              console.log('âœ… Answers synced from server');
            }

            // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ñ‚ÐµÐºÑƒÑ‰Ð¸Ðµ Ñ‚ÐµÑÑ‚Ñ‹ Ð½Ð° ÑÐµÑ€Ð²ÐµÑ€
            for (const dayData of finalTests) {
              await userAPI.saveTasks(dayData.day, dayData.tasks).catch(() => {});
            }

            console.log('âœ… Background sync completed');
          } catch (syncError) {
            console.log('Background sync failed, continuing with local data');
          }
        }, 2000);
      }

    } catch (error) {
      console.error('Error loading user data:', error);

      // ÐÐ’ÐÐ Ð˜Ð™ÐÐ«Ð™ Ð Ð•Ð–Ð˜Ðœ: Ð³ÐµÐ½ÐµÑ€Ð¸Ñ€ÑƒÐµÐ¼ Ð±Ð°Ð·Ð¾Ð²Ñ‹Ðµ Ð·Ð°Ð´Ð°Ð½Ð¸Ñ
      const emergencyTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
      })).filter(day => day.tasks.length > 0);

      setTests(emergencyTests);
      setAnswers({});
      localStorage.setItem('userMathTests', JSON.stringify(emergencyTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));

      setDataLoaded(true);
    }
  }, [user, t, dataLoaded]);

  // ðŸŸ¢ Ð—ÐÐ“Ð Ð£Ð—ÐšÐ Ð”ÐÐÐÐ«Ð¥ ÐŸÐ Ð˜ Ð˜Ð—ÐœÐ•ÐÐ•ÐÐ˜Ð˜ ÐŸÐžÐ›Ð¬Ð—ÐžÐ’ÐÐ¢Ð•Ð›Ð¯ Ð˜Ð›Ð˜ Ð¯Ð—Ð«ÐšÐ
  useEffect(() => {
    if (!loading && user !== null) {
      loadUserData();
    }
  }, [user, loading, loadUserData]);

  useEffect(() => {
    if (dataLoaded && tests.length > 0) {
      // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´Ñ‹ Ð¿Ñ€Ð¸ ÑÐ¼ÐµÐ½Ðµ ÑÐ·Ñ‹ÐºÐ°
      const updatedTests = tests.map(dayData => ({
        ...dayData,
        tasks: applyTranslationsToTasks(dayData.tasks, t)
      }));
      setTests(updatedTests);
    }
  }, [i18n.language, t]);


// ðŸŸ¢ ÐšÐ Ð˜Ð¢Ð˜Ð§Ð•Ð¡ÐšÐ˜Ð™ FIX: Ð’ÐžÐ¡Ð¡Ð¢ÐÐÐžÐ’Ð›Ð•ÐÐ˜Ð• ÐŸÐ ÐžÐ“Ð Ð•Ð¡Ð¡Ð ÐŸÐ Ð˜ Ð¡Ð‘Ð ÐžÐ¡Ð• ÐšÐ­Ð¨Ð SAFARI
const recoverProgressAfterCacheClear = useCallback(async () => {
  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼: Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½, Ð½Ð¾ Ð² localStorage Ð½ÐµÑ‚ Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑÐ°
  const hasAuthToken = localStorage.getItem('authToken');
  const hasLocalTests = localStorage.getItem('userMathTests');
  const hasLocalAnswers = localStorage.getItem('userMathAnswers');

  if (hasAuthToken && (!hasLocalTests || !hasLocalAnswers)) {
    console.log('ðŸ”„ ÐžÐ±Ð½Ð°Ñ€ÑƒÐ¶ÐµÐ½ ÑÐ±Ñ€Ð¾Ñ ÐºÑÑˆÐ° Safari - Ð²Ð¾ÑÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑ Ñ ÑÐµÑ€Ð²ÐµÑ€Ð°...');

    try {
      // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑ Ñ ÑÐµÑ€Ð²ÐµÑ€Ð°
      const [serverAnswers, serverTasks] = await Promise.all([
        userAPI.getAnswers('all').catch(() => ({ data: {} })),
        userAPI.getTasks('all').catch(() => ({ data: {} }))
