      } else {
        // Ð•ÑÐ»Ð¸ Ð½Ð° ÑÐµÑ€Ð²ÐµÑ€Ðµ Ð½ÐµÑ‚ Ð·Ð°Ð´Ð°Ð½Ð¸Ð¹ - Ð³ÐµÐ½ÐµÑ€Ð¸Ñ€ÑƒÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ðµ
        recoveredTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
