import axios from 'axios';

// ðŸŸ¢ Ð˜Ð¡ÐŸÐ ÐÐ’Ð›Ð•ÐÐž: Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÐ¼ proxy Ð´Ð»Ñ Ñ€Ð°Ð·Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ¸
const API_BASE_URL = process.env.NODE_ENV === 'development' 
  ? '/api'  // Ð‘ÑƒÐ´ÐµÑ‚ Ð¿Ñ€Ð¾ÐºÑÐ¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒÑÑ Ñ‡ÐµÑ€ÐµÐ· React dev server
  : 'http://217.160.126.178:8001/api';

// Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ñ‚Ð¾ÐºÐµÐ½Ð°
const getToken = () => {
  return localStorage.getItem('authToken');
};

// Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ðµ Ð½Ð°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ¸ axios
const api = axios.create({
  baseURL: API_BASE_URL,
});

// Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ interceptor Ð´Ð»Ñ Ð°Ð²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ð¹ Ð¿Ð¾Ð´ÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ¸ Ñ‚Ð¾ÐºÐµÐ½Ð°
api.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }

  // ðŸŸ¢ Ð”ÐžÐ‘ÐÐ’Ð›Ð•ÐÐž: CORS headers
  config.headers['Content-Type'] = 'application/json';
  config.headers['Accept'] = 'application/json';

  return config;
});

// ðŸŸ¢ Ð”ÐžÐ‘ÐÐ’Ð›Ð•ÐÐž: ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð¾ÑˆÐ¸Ð±Ð¾Ðº
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

// API Ð´Ð»Ñ Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸
export const authAPI = {
  register: (userData) => api.post('/auth/register', userData),
  login: (credentials) => api.post('/auth/login', credentials),
  getMe: () => api.get('/users/me'),
};

// API Ð´Ð»Ñ ÐºÑƒÑ€ÑÐ°
export const courseAPI = {
  getStructure: () => api.get('/course/structure'),
  generateTasks: (dayNumber) => api.get(`/course/day/${dayNumber}/tasks`),
  getProgress: () => api.get('/course/progress'),
  updateProgress: (day, answers, completed) => 
    api.post(`/course/day/${day}/progress`, { answers, completed_tasks: completed }),
  resetProgress: () => api.delete('/course/progress'),
};

// API Ð´Ð»Ñ Ð·Ð°Ð´Ð°Ð½Ð¸Ð¹ Ð¸ Ð¾Ñ‚Ð²ÐµÑ‚Ð¾Ð² Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
export const userAPI = {
  // Ð—Ð°Ð´Ð°Ð½Ð¸Ñ
  getTasks: (day) => api.get(`/user/tasks/${day}`),
  saveTasks: (day, tasks) => api.post(`/user/tasks/${day}`, tasks),

  // ÐžÑ‚Ð²ÐµÑ‚Ñ‹
  getAnswers: (day) => 
    day === 'all' 
      ? api.get('/user/all-answers')
      : api.get(`/user/answers/${day}`),

  saveAnswer: (day, taskIndex, answer, isCorrect = false) => 
    api.post(`/user/answer/${day}/${taskIndex}`, { answer, is_correct: isCorrect }),
};

export default api;
