import React, { useEffect, useState, useCallback } from "react";
import Header from "./components/Header";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { authAPI, courseAPI, userAPI } from './services/api';
import { courseStructure } from './taskGenerators';
import * as taskGenerators from './taskGenerators';
import { useTranslation } from 'react-i18next';
import DayNavigation from './components/DayNavigation';

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è –ø–µ—Ä–µ–≤–æ–¥–æ–≤ –∫ –∑–∞–¥–∞–Ω–∏—è–º
const applyTranslationsToTasks = (tasks, t) => {
  if (!tasks || !Array.isArray(tasks)) return [];

  return tasks.map(task => ({
    ...task,
    question: task.translationKey ? t(task.translationKey, task.translationParams) : task.question,
    explanation: task.explanationKey ? t(task.explanationKey, task.explanationParams) : task.explanation,
    answerFormat: task.formatKey ? t(task.formatKey) : task.answerFormat
  }));
};

// üü¢ –§–£–ù–ö–¶–ò–Ø –ü–†–û–í–ï–†–ö–ò –ü–†–ê–í–ò–õ–¨–ù–û–°–¢–ò –û–¢–í–ï–¢–ê
const isCorrectAnswerCheck = (day, index, correctAnswer, currentAnswers) => {
  if (!currentAnswers) return false;

  const userAnswer = currentAnswers[`${day}-${index}`]?.toString().trim().toLowerCase();
  const correct = correctAnswer?.toString().toLowerCase();

  if (!userAnswer || !correct) return false;

  if (correct.includes('%')) {
    const userNum = userAnswer.replace('%', '');
    const correctNum = correct.replace('%', '');
    return userNum === correctNum;
  }

  if (correct.includes('/')) {
    const [userA, userB] = userAnswer.split('/');
    const [correctA, correctB] = correct.split('/');
    return userA === correctA && userB === correctB;
  }

  return userAnswer === correct;
};

function App() {
  const { t, i18n } = useTranslation();
  const [tests, setTests] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [showResetConfirm, setShowResetConfirm] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [currentDay, setCurrentDay] = useState(1);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [authError, setAuthError] = useState('');
  const [dataLoaded, setDataLoaded] = useState(false);

  // üü¢ EFFECT –î–õ–Ø –û–¢–°–õ–ï–ñ–ò–í–ê–ù–ò–Ø –°–ö–†–û–õ–õ–ê
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 300);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // üü¢ –ù–ê–î–ï–ñ–ù–ê–Ø –ü–†–û–í–ï–†–ö–ê –ê–£–¢–ï–ù–¢–ò–§–ò–ö–ê–¶–ò–ò
  const checkAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');

      if (!token) {
        setUser({ name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false });
        setLoading(false);
        return;
      }

      // –ü—Ä–æ–±—É–µ–º –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      try {
        const response = await authAPI.getMe();
        const userData = {
          ...response.data,
          isLoggedIn: true
        };
        setUser(userData);
        localStorage.setItem('userData', JSON.stringify(userData));
      } catch (error) {
        console.log('getMe failed, checking localStorage for user data');

        // –ü—Ä–æ–±—É–µ–º –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –∏–∑ localStorage
        const savedUser = localStorage.getItem('userData');
        if (savedUser) {
          const userData = JSON.parse(savedUser);
          setUser({ ...userData, isLoggedIn: true });
        } else {
          // –°–æ–∑–¥–∞–µ–º –≤—Ä–µ–º–µ–Ω–Ω–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
          const tempUser = {
            id: Date.now(),
            name: '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å',
            email: 'user@example.com',
            isLoggedIn: true
          };
          setUser(tempUser);
          localStorage.setItem('userData', JSON.stringify(tempUser));
        }
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setUser({ name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false });
    } finally {
      setLoading(false);
    }
  }, [t]);

  // üü¢ –ó–ê–ì–†–£–ó–ö–ê –ü–†–ò –°–¢–ê–†–¢–ï
  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  // üü¢ –ù–ê–î–ï–ñ–ù–ê–Ø –ó–ê–ì–†–£–ó–ö–ê –î–ê–ù–ù–´–•
  const loadUserData = useCallback(async () => {
    if (dataLoaded) return;

    try {
      // 1. –°–Ω–∞—á–∞–ª–∞ –∑–∞–≥—Ä—É–∂–∞–µ–º –∏–∑ localStorage (–º–≥–Ω–æ–≤–µ–Ω–Ω–æ)
      const savedTests = localStorage.getItem('userMathTests');
      const savedAnswers = localStorage.getItem('userMathAnswers');

      let finalTests = [];
      let finalAnswers = {};

      if (savedTests && savedAnswers) {
        try {
          const parsedTests = JSON.parse(savedTests);
          const parsedAnswers = JSON.parse(savedAnswers);

          finalTests = parsedTests.map(dayData => ({
            ...dayData,
            tasks: applyTranslationsToTasks(dayData.tasks, t)
          }));
          finalAnswers = parsedAnswers;
        } catch (parseError) {
          console.error('Error parsing localStorage data:', parseError);
          // –ï—Å–ª–∏ –æ—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ - –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –Ω–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ
          throw new Error('Invalid localStorage data');
        }
      } else {
        // 2. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –Ω–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è
        finalTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
        })).filter(day => day.tasks.length > 0);

        finalAnswers = {};
      }

      // 3. –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ
      setTests(finalTests);
      setAnswers(finalAnswers);

      // 4. –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ localStorage
      localStorage.setItem('userMathTests', JSON.stringify(finalTests));
      localStorage.setItem('userMathAnswers', JSON.stringify(finalAnswers));

      setDataLoaded(true);

      // 5. –§–û–ù–æ–≤–∞—è —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º –¥–ª—è –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
      if (user && user.isLoggedIn) {
        setTimeout(async () => {
          try {
            console.log('üîÑ Starting background sync with server...');

            const [serverAnswers, serverProgress] = await Promise.all([
              userAPI.getAnswers('all').catch(() => ({ data: {} })),
              courseAPI.getProgress().catch(() => ({ data: {} }))
            ]);

            // –ï—Å–ª–∏ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ –µ—Å—Ç—å –æ—Ç–≤–µ—Ç—ã - –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏—Ö
            if (serverAnswers.data && Object.keys(serverAnswers.data).length > 0) {
              setAnswers(serverAnswers.data);
              localStorage.setItem('userMathAnswers', JSON.stringify(serverAnswers.data));
              console.log('‚úÖ Answers synced from server');
            }

            // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–µ–∫—É—â–∏–µ —Ç–µ—Å—Ç—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä
            for (const dayData of finalTests) {
              await userAPI.saveTasks(dayData.day, dayData.tasks).catch(() => {});
            }

            console.log('‚úÖ Background sync completed');
          } catch (syncError) {
            console.log('Background sync failed, continuing with local data');
          }
        }, 2000);
      }

    } catch (error) {
      console.error('Error loading user data:', error);

      // –ê–í–ê–†–ò–ô–ù–´–ô –†–ï–ñ–ò–ú: –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –±–∞–∑–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è
      const emergencyTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
      })).filter(day => day.tasks.length > 0);

      setTests(emergencyTests);
      setAnswers({});
      localStorage.setItem('userMathTests', JSON.stringify(emergencyTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));

      setDataLoaded(true);
    }
  }, [user, t, dataLoaded]);

  // üü¢ –ó–ê–ì–†–£–ó–ö–ê –î–ê–ù–ù–´–• –ü–†–ò –ò–ó–ú–ï–ù–ï–ù–ò–ò –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø –ò–õ–ò –Ø–ó–´–ö–ê
  useEffect(() => {
    if (!loading && user !== null) {
      loadUserData();
    }
  }, [user, loading, loadUserData]);

  useEffect(() => {
    if (dataLoaded && tests.length > 0) {
      // –û–±–Ω–æ–≤–ª—è–µ–º –ø–µ—Ä–µ–≤–æ–¥—ã –ø—Ä–∏ —Å–º–µ–Ω–µ —è–∑—ã–∫–∞
      const updatedTests = tests.map(dayData => ({
        ...dayData,
        tasks: applyTranslationsToTasks(dayData.tasks, t)
      }));
      setTests(updatedTests);
    }
  }, [i18n.language, t]);


// üü¢ –ö–†–ò–¢–ò–ß–ï–°–ö–ò–ô FIX: –í–û–°–°–¢–ê–ù–û–í–õ–ï–ù–ò–ï –ü–†–û–ì–†–ï–°–°–ê –ü–†–ò –°–ë–†–û–°–ï –ö–≠–®–ê SAFARI
const recoverProgressAfterCacheClear = useCallback(async () => {
  // –ü—Ä–æ–≤–µ—Ä—è–µ–º: –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω, –Ω–æ –≤ localStorage –Ω–µ—Ç –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
  const hasAuthToken = localStorage.getItem('authToken');
  const hasLocalTests = localStorage.getItem('userMathTests');
  const hasLocalAnswers = localStorage.getItem('userMathAnswers');

  if (hasAuthToken && (!hasLocalTests || !hasLocalAnswers)) {
    console.log('üîÑ –û–±–Ω–∞—Ä—É–∂–µ–Ω —Å–±—Ä–æ—Å –∫—ç—à–∞ Safari - –≤–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å —Å —Å–µ—Ä–≤–µ—Ä–∞...');

    try {
      // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å —Å —Å–µ—Ä–≤–µ—Ä–∞
      const [serverAnswers, serverTasks] = await Promise.all([
        userAPI.getAnswers('all').catch(() => ({ data: {} })),
        userAPI.getTasks('all').catch(() => ({ data: {} }))
      ]);

      let recoveredTests = [];
      let recoveredAnswers = serverAnswers.data || {};

      // –ï—Å–ª–∏ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ –µ—Å—Ç—å –∑–∞–¥–∞–Ω–∏—è - –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏—Ö
      if (serverTasks.data && Object.keys(serverTasks.data).length > 0) {
        recoveredTests = Object.values(serverTasks.data).map(dayData => ({
          ...dayData,
          tasks: applyTranslationsToTasks(dayData.tasks, t)
        }));
      } else {
        // –ï—Å–ª–∏ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ –Ω–µ—Ç –∑–∞–¥–∞–Ω–∏–π - –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –Ω–æ–≤—ã–µ
        recoveredTests = courseStructure.map(day => ({
          day: day.day,
          titleKey: day.titleKey,
          theoryLink: day.theoryLink,
          tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
        })).filter(day => day.tasks.length > 0);
      }

      // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ
      setTests(recoveredTests);
      setAnswers(recoveredAnswers);

      // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º localStorage
      localStorage.setItem('userMathTests', JSON.stringify(recoveredTests));
      localStorage.setItem('userMathAnswers', JSON.stringify(recoveredAnswers));

      console.log('‚úÖ –ü—Ä–æ–≥—Ä–µ—Å—Å –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω –ø–æ—Å–ª–µ —Å–±—Ä–æ—Å–∞ –∫—ç—à–∞ Safari');

    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è –ø—Ä–æ–≥—Ä–µ—Å—Å–∞:', error);

      // –ê–≤–∞—Ä–∏–π–Ω–æ–µ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ - –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –Ω–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è
      const emergencyTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
      })).filter(day => day.tasks.length > 0);

      setTests(emergencyTests);
      localStorage.setItem('userMathTests', JSON.stringify(emergencyTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));
    }
  }
}, [t]);

// üü¢ –ó–ê–ü–£–°–ö –í–û–°–°–¢–ê–ù–û–í–õ–ï–ù–ò–Ø –ü–†–ò –ó–ê–ì–†–£–ó–ö–ï
useEffect(() => {
  recoverProgressAfterCacheClear();
}, [recoverProgressAfterCacheClear]);

// üü¢ –ü–û–°–¢–û–Ø–ù–ù–´–ô –ú–û–ù–ò–¢–û–†–ò–ù–ì –î–õ–Ø SAFARI - –ó–ê–ü–£–°–ö–ê–ï–¢–°–Ø –ö–ê–ñ–î–´–ï 5 –°–ï–ö–£–ù–î
useEffect(() => {
  const interval = setInterval(() => {
    const hasAuthToken = localStorage.getItem('authToken');
    const hasLocalTests = localStorage.getItem('userMathTests');
    const hasTests = tests.length > 0;

    // –ï—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω, –Ω–æ –Ω–µ—Ç —Ç–µ—Å—Ç–æ–≤ - –í–û–°–°–¢–ê–ù–ê–í–õ–ò–í–ê–ï–ú
    if (hasAuthToken && (!hasLocalTests || !hasTests)) {
      console.log('üö® –ú–û–ù–ò–¢–û–†–ò–ù–ì: –û–±–Ω–∞—Ä—É–∂–µ–Ω–∞ –ø–æ—Ç–µ—Ä—è –¥–∞–Ω–Ω—ã—Ö - –≤–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º...');

      // –ó–∞–ø—É—Å–∫–∞–µ–º –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ
      recoverProgressAfterCacheClear();
    }
  }, 5000); // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–∞–∂–¥—ã–µ 5 —Å–µ–∫—É–Ω–¥

  return () => clearInterval(interval);
}, [tests.length, recoverProgressAfterCacheClear]);



  // üü¢ –ù–ê–î–ï–ñ–ù–´–ô –û–ë–†–ê–ë–û–¢–ß–ò–ö –í–•–û–î–ê
  const handleLogin = async (formData) => {
    setAuthError('');

    try {
      console.log('üîê Attempting login...');
      const response = await authAPI.login({ 
        email: formData.email, 
        password: formData.password 
      });

      if (!response.data?.access_token) {
        throw new Error('No access token received');
      }

      localStorage.setItem('authToken', response.data.access_token);
      console.log('‚úÖ Login successful, token saved');

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º —Ñ–æ—Ä–º—É –≤—Ö–æ–¥–∞
      setShowLogin(false);

      // –û–±–Ω–æ–≤–ª—è–µ–º –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—é
      await checkAuth();

    } catch (error) {
      console.error('‚ùå Login error:', error);
      const errorMessage = error.response?.data?.detail || error.message || '–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞';
      setAuthError(errorMessage);

      // –û—á–∏—â–∞–µ–º –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–π —Ç–æ–∫–µ–Ω
      localStorage.removeItem('authToken');
    }
  };

  // üü¢ –ù–ê–î–ï–ñ–ù–´–ô –û–ë–†–ê–ë–û–¢–ß–ò–ö –†–ï–ì–ò–°–¢–†–ê–¶–ò–ò
  const handleRegister = async (formData) => {
    setAuthError('');

    try {
      console.log('üìù Attempting registration...');

      await authAPI.register({ 
        email: formData.email,
        username: formData.username, 
        password: formData.password,
        grade: formData.grade
      });

      console.log('‚úÖ Registration successful, attempting login...');

      const loginResponse = await authAPI.login({
        email: formData.email,
        password: formData.password
      });

      if (!loginResponse.data?.access_token) {
        throw new Error('No access token received after registration');
      }

      localStorage.setItem('authToken', loginResponse.data.access_token);
      console.log('‚úÖ Auto-login successful');

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º —Ñ–æ—Ä–º—É —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
      setShowRegister(false);

      // –û–±–Ω–æ–≤–ª—è–µ–º –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—é
      await checkAuth();

    } catch (error) {
      console.error('‚ùå Registration error:', error);
      const errorMessage = error.response?.data?.detail || error.message || '–û—à–∏–±–∫–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏';
      setAuthError(errorMessage);

      // –û—á–∏—â–∞–µ–º –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–π —Ç–æ–∫–µ–Ω
      localStorage.removeItem('authToken');
    }
  };

  // üü¢ –û–ë–†–ê–ë–û–¢–ß–ò–ö –í–´–•–û–î–ê
  const handleLogout = () => {
    console.log('üö™ Logging out...');
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    setUser({ name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false });
    setAuthError('');
    // –ü—Ä–æ–≥—Ä–µ—Å—Å –æ—Å—Ç–∞–µ—Ç—Å—è –≤ localStorage –¥–ª—è –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è
  };

  // üü¢ –û–ü–¢–ò–ú–ò–ó–ò–†–û–í–ê–ù–ù–ê–Ø –û–ë–†–ê–ë–û–¢–ö–ê –í–í–û–î–ê –û–¢–í–ï–¢–ê
  const handleAnswerChange = async (day, index, value) => {
    const answerKey = `${day}-${index}`;
    const newAnswers = {
      ...answers,
      [answerKey]: value
    };

    // –ù–µ–º–µ–¥–ª–µ–Ω–Ω–æ –æ–±–Ω–æ–≤–ª—è–µ–º UI
    setAnswers(newAnswers);
    localStorage.setItem('userMathAnswers', JSON.stringify(newAnswers));

    // –§–æ–Ω–æ–≤–∞—è —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º
    if (user && user.isLoggedIn) {
      setTimeout(async () => {
        try {
          const dayData = tests.find(d => d.day === day);
          if (dayData && dayData.tasks[index]) {
            const task = dayData.tasks[index];
            const isCorrectAnswer = isCorrectAnswerCheck(day, index, task.answer, newAnswers);

            await userAPI.saveAnswer(day, index, value, isCorrectAnswer);

            const completedTasks = dayData.tasks.filter((t, i) => 
              isCorrectAnswerCheck(day, i, t.answer, newAnswers)
            ).length;

            await courseAPI.updateProgress(day, newAnswers, completedTasks);
          }
        } catch (error) {
          console.log('Background answer sync failed');
        }
      }, 500);
    }
  };

  // üü¢ –í–´–ß–ò–°–õ–ï–ù–ò–ï –ü–†–û–ì–†–ï–°–°–ê
  const getDayProgress = (day, tasks) => {
    if (!tasks || !Array.isArray(tasks)) return 0;

    const correctCount = tasks.filter((task, index) =>
      isCorrectAnswerCheck(day, index, task.answer, answers)
    ).length;

    return tasks.length > 0 ? Math.round((correctCount / tasks.length) * 100) : 0;
  };

  const getTotalProgress = () => {
    let totalCorrect = 0;
    let totalTasks = 0;

    tests.forEach(dayData => {
      if (dayData.tasks && Array.isArray(dayData.tasks)) {
        dayData.tasks.forEach((task, index) => {
          totalTasks++;
          if (isCorrectAnswerCheck(dayData.day, index, task.answer, answers)) {
            totalCorrect++;
          }
        });
      }
    });

    return totalTasks > 0 ? Math.round((totalCorrect / totalTasks) * 100) : 0;
  };

  // üü¢ –°–ë–†–û–° –ü–†–û–ì–†–ï–°–°–ê
  const resetProgress = async () => {
    try {
      const userTests = courseStructure.map(day => ({
        day: day.day,
        titleKey: day.titleKey,
        theoryLink: day.theoryLink,
        tasks: taskGenerators[day.generator] ? taskGenerators[day.generator](day.taskCount, t) : []
      })).filter(day => day.tasks.length > 0);

      setTests(userTests);
      setAnswers({});

      localStorage.setItem('userMathTests', JSON.stringify(userTests));
      localStorage.setItem('userMathAnswers', JSON.stringify({}));

      // –§–æ–Ω–æ–≤–∞—è —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º
      if (user && user.isLoggedIn) {
        setTimeout(async () => {
          try {
            await courseAPI.resetProgress();
            for (const dayData of userTests) {
              await userAPI.saveTasks(dayData.day, dayData.tasks);
            }
          } catch (error) {
            console.log('Background reset sync failed');
          }
        }, 500);
      }

    } catch (error) {
      console.error('Error resetting progress:', error);
    }
  };

  // üü¢ –û–ë–†–ê–ë–û–¢–ß–ò–ö–ò UI
  const handleLoginClick = () => {
    setShowLogin(true);
    setAuthError('');
  };

  const handleRegisterClick = () => {
    setShowRegister(true);
    setAuthError('');
  };

  const handleDayClick = (dayNumber) => {
    setCurrentDay(dayNumber);
    const element = document.getElementById(`day-${dayNumber}`);
    if (element) {
      const headerOffset = 340;
      const elementPosition = element.offsetTop;
      const offsetPosition = elementPosition - headerOffset;

      setTimeout(() => {
        window.scrollTo({
          top: Math.max(0, offsetPosition),
          behavior: 'smooth'
        });
      }, 50);
    }
  };

  // üü¢ –ó–ê–ì–†–£–ó–û–ß–ù–´–ô –≠–ö–†–ê–ù
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">{t('loading', "–ó–∞–≥—Ä—É–∑–∫–∞...")}</p>
        </div>
      </div>
    );
  }

  // üü¢ –û–°–ù–û–í–ù–û–ô RENDER
  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-200">
      <Header 
        user={user || { name: t('header.guest', "–ì–æ—Å—Ç—å"), isLoggedIn: false }}
        progress={getTotalProgress()}
        onResetClick={() => setShowResetConfirm(true)}
        onSettingsClick={() => console.log("Settings clicked")}
        onLoginClick={handleLoginClick}
        onLogoutClick={handleLogout}
        grade="6"
      />

      {/* –ú–û–î–ê–õ–¨–ù–´–ï –û–ö–ù–ê */}
      {showLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <Login 
            onLogin={handleLogin} 
            onSwitchToRegister={() => { 
              setShowLogin(false); 
              setShowRegister(true); 
              setAuthError('');
            }}
            error={authError}
          />
        </div>
      )}

      {showRegister && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <Register 
            onRegister={handleRegister} 
            onSwitchToLogin={() => { 
              setShowRegister(false); 
              setShowLogin(true); 
              setAuthError('');
            }}
            error={authError}
          />
        </div>
      )}

      {/* –ù–ê–í–ò–ì–ê–¶–ò–Ø –ü–û –î–ù–Ø–ú */}
      {user && user.isLoggedIn && tests.length > 0 && (
        <DayNavigation 
          days={tests.map(day => ({
            ...day,
            progress: getDayProgress(day.day, day.tasks)
          }))}
          currentDay={currentDay}
          onDayClick={handleDayClick}
        />
      )}

      {/* –û–°–ù–û–í–ù–û–ô –ö–û–ù–¢–ï–ù–¢ */}
      {user && user.isLoggedIn ? (
        <div className="container mx-auto p-14 max-w-4xl">
          <div className="grid gap-16">
            {tests.map(dayData => {
              const dayProgress = getDayProgress(dayData.day, dayData.tasks);

              return (
                <div key={dayData.day} id={`day-${dayData.day}`} className="bg-white dark:bg-gray-800 rounded-xl shadow-md p-6">
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex-1 min-w-0">
                      <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-1">
                        {t('days.day', "–î–µ–Ω—å")} {dayData.day}
                      </h2>
                      <p className="text-lg text-gray-800 dark:text-gray-200 font-medium">
                        {t(dayData.titleKey)}
                      </p>
                    </div>

                    <div className="flex flex-col items-end space-y-1 flex-shrink-0">
                      <div className="flex items-center space-x-2">
                        <span className="text-sm font-semibold text-gray-600 dark:text-gray-400">
                          {dayProgress}%
                        </span>
                        <div className="w-20 bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                          <div 
                            className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                            style={{ width: `${dayProgress}%` }}
                          />
                        </div>
                      </div>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        {dayData.tasks?.length || 0} {t('days.tasks', "–∑–∞–¥–∞–Ω–∏–π")}
                      </p>
                    </div>
                  </div>

                  <div className="space-y-3">
                    {dayData.tasks?.map((task, index) => {
                      const isAnswerCorrect = isCorrectAnswerCheck(dayData.day, index, task.answer, answers);
                      const hasAnswer = answers[`${dayData.day}-${index}`];

                      return (
                        <div key={index} className={`p-4 rounded-lg ${
                          hasAnswer 
                            ? isAnswerCorrect 
                              ? 'bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800'
                              : 'bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800'
                            : 'bg-gray-50 dark:bg-gray-700'
                        }`}>
                          <div className="flex items-center space-x-3">
                            <span className="flex-1 text-gray-800 dark:text-gray-200">
                              <strong>{t('tasks.task', "–ó–∞–¥–∞—á–∞")} {index + 1}:</strong> {task.question}
                            </span>

                            <div className="flex items-center space-x-2">
                              <input
                                type="text"
                                placeholder={t('answer', "–û—Ç–≤–µ—Ç")}
                                value={answers[`${dayData.day}-${index}`] || ""}
                                onChange={e => handleAnswerChange(dayData.day, index, e.target.value)}
                                className="w-24 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-800 text-gray-900 dark:text-white text-center"
                              />
                              {hasAnswer && (
                                isAnswerCorrect ? (
                                  <span className="text-green-500 text-xl" title={t('correct', "–ü—Ä–∞–≤–∏–ª—å–Ω–æ!")}>‚úÖ</span>
                                ) : (
                                  <span className="text-red-500 text-xl" title={t('try_again', "–ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑")}>‚ùå</span>
                                )
                              )}
                            </div>
                          </div>

                          {task.answerFormat && (
                            <div className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                              üí° {t('answerFormat.hint', "–§–æ—Ä–º–∞—Ç –æ—Ç–≤–µ—Ç–∞")}: {task.answerFormat}
                            </div>
                          )}

                          {hasAnswer && !isAnswerCorrect && task.explanation && (
                            <div className="mt-2 p-2 bg-blue-50 dark:bg-blue-900/20 rounded-md">
                              <p className="text-sm text-blue-800 dark:text-blue-200">
                                üí° {task.explanation}
                              </p>
                            </div>
                          )}
                        </div>
                      );
                    })}
                  </div>

                  {dayData.theoryLink && (
                    <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-600">
                      <a
                        href={dayData.theoryLink}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 dark:text-blue-400 hover:underline text-sm flex items-center"
                      >
                        üìñ {t('days.theory', "–¢–µ–æ—Ä–µ—Ç–∏—á–µ—Å–∫–∏–µ –º–∞—Ç–µ—Ä–∏–∞–ª—ã")} "{t(dayData.titleKey)}"
                      </a>
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          <div className="mt-8 text-center text-gray-500 dark:text-gray-400 text-sm">
            <p>{t('footer.course_description', "–ö—É—Ä—Å –¥–ª—è –∑–∞–∫—Ä–µ–ø–ª–µ–Ω–∏—è –∑–Ω–∞–Ω–∏–π –ø–æ –º–∞—Ç–µ–º–∞—Ç–∏–∫–µ –∑–∞ 6 –∫–ª–∞—Å—Å")}</p>
            <p>¬© 2025 Math Academy | {t('footer.generated', "–ó–∞–¥–∞–Ω–∏—è –≥–µ–Ω–µ—Ä–∏—Ä—É—é—Ç—Å—è —Å–ª—É—á–∞–π–Ω–æ")}</p>
          </div>
        </div>
      ) : (
        // üü¢ –≠–ö–†–ê–ù –ü–†–ò–í–ï–¢–°–¢–í–ò–Ø –î–õ–Ø –ù–ï–ê–í–¢–û–†–ò–ó–û–í–ê–ù–ù–´–• –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–ï–ô
        <div className="min-h-screen flex items-center justify-center px-4">
          <div className="text-center max-w-2xl mx-auto">
            <div className="mb-8 flex justify-center">
              <div className="w-32 h-32 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white text-6xl">
                üìö
              </div>
            </div>

            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-6">
              {t('auth.welcome', "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ Math Academy!")}
            </h1>

            <p className="text-xl text-gray-600 dark:text-gray-400 mb-8 max-w-md mx-auto leading-relaxed">
              {t('auth.prompt', "–í–æ–π–¥–∏—Ç–µ –∏–ª–∏ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–π—Ç–µ—Å—å —á—Ç–æ–±—ã –Ω–∞—á–∞—Ç—å –æ–±—É—á–µ–Ω–∏–µ")}
            </p>

            <div className="flex flex-col sm:flex-row gap-4 justify-center mb-12">
              <button
                onClick={handleLoginClick}
                className="bg-blue-500 hover:bg-blue-600 text-white px-8 py-3 rounded-lg font-medium text-lg transition-colors shadow-lg"
              >
                {t('header.login', "–í–æ–π—Ç–∏")}
              </button>
              <button
                onClick={handleRegisterClick}
                className="bg-green-500 hover:bg-green-600 text-white px-8 py-3 rounded-lg font-medium text-lg transition-colors shadow-lg"
              >
                {t('header.register', "–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è")}
              </button>
            </div>

            <div className="border-t border-gray-200 dark:border-gray-700 pt-8">
              <p className="text-lg text-gray-500 dark:text-gray-400 italic">
                {t('slogan', "–û—Ç –º–∞—Ç–µ–º–∞—Ç–∏—á–µ—Å–∫–∏—Ö –æ—Å–Ω–æ–≤ –∫ –æ–±—Ä–∞–∑–æ–≤–∞—Ç–µ–ª—å–Ω–æ–π —ç–∫–æ—Å–∏—Å—Ç–µ–º–µ! üöÄ")}
              </p>
            </div>
          </div>
        </div>
      )}

      {/* –ü–û–î–¢–í–ï–†–ñ–î–ï–ù–ò–ï –°–ë–†–û–°–ê –ü–†–û–ì–†–ï–°–°–ê */}
      {showResetConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 rounded-lg p-6 m-4 max-w-md w-full">
            <h3 className="text-lg font-bold text-gray-900 dark:text-white mb-2">
              üîÑ {t('reset.new_tasks', "–ù–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è")}
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              {t('reset.confirmation', "–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å –Ω–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è? –í–µ—Å—å —Ç–µ–∫—É—â–∏–π –ø—Ä–æ–≥—Ä–µ—Å—Å –±—É–¥–µ—Ç —Å–±—Ä–æ—à–µ–Ω.")}
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowResetConfirm(false)}
                className="flex-1 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
              >
                {t('reset.cancel', "–û—Ç–º–µ–Ω–∞")}
              </button>
              <button
                onClick={() => {
                  resetProgress();
                  setShowResetConfirm(false);
                }}
                className="flex-1 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-lg transition-colors"
              >
                {t('reset.generate', "–°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å")}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* –ö–ù–û–ü–ö–ê –ù–ê–í–ï–†–• */}
      {showScrollTop && (
        <button 
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          className="fixed bottom-6 right-6 z-50 bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-500 rounded-full w-10 h-10 flex items-center justify-center shadow-lg hover:bg-gray-50 dark:hover:bg-gray-700 transition-all text-gray-600 dark:text-gray-300 hover:text-gray-800 dark:hover:text-gray-100"
          aria-label="–ù–∞–≤–µ—Ä—Ö"
        >
          ‚Üë
        </button>
      )}
    </div>
  );
}

export default App;
